int32 OS_SymbolTableDump(const char *filename, size_t SizeLimit)
{
    int32             return_code;
    char              translated_path[OS_MAX_LOCAL_PATH_LEN];
    OS_object_token_t token;

    /* Logging probe: record entry and parameters */
    {
        FILE *logf = fopen("/tmp/logs/353.txt", "a");
        if (logf != NULL)
        {
            fprintf(logf, "ENTER OS_SymbolTableDump: filename=%p, SizeLimit=%zu\n", (const void *)filename, SizeLimit);
            fclose(logf);
        }
    }

    /* Check parameters */
    OS_CHECK_POINTER(filename);

    /* Log that pointer check passed (if execution continues) */
    {
        FILE *logf = fopen("/tmp/logs/353.txt", "a");
        if (logf != NULL)
        {
            fprintf(logf, "OS_CHECK_POINTER passed: filename=%p\n", (const void *)filename);
            fclose(logf);
        }
    }

    /*
    ** Translate the filename to the Host System
    */
    return_code = OS_TranslatePath(filename, translated_path);

    /* Log translation results */
    {
        FILE *logf = fopen("/tmp/logs/353.txt", "a");
        if (logf != NULL)
        {
            fprintf(logf, "OS_TranslatePath returned %d\n", (int)return_code);
            if (return_code == OS_SUCCESS)
            {
                /* Print the translated path safely */
                fprintf(logf, "translated_path (len<=%d): '%s'\n", (int)OS_MAX_LOCAL_PATH_LEN, translated_path);
            }
            fclose(logf);
        }
    }

    if (return_code != OS_SUCCESS)
    {
        /* Log early return */
        FILE *logf = fopen("/tmp/logs/353.txt", "a");
        if (logf != NULL)
        {
            fprintf(logf, "Early return from OS_SymbolTableDump due to OS_TranslatePath failure: %d\n", (int)return_code);
            fclose(logf);
        }
        return return_code;
    }

    /*
     * Locking the global ensures only one symbol table dump
     * can be executing.  It also prevents module loading/unloading
     * while the dump is occurring.
     *
     * Because calls to this function are serialized, the
     * underlying implementation may safely use globals for
     * state storage.
     */
    return_code = OS_ObjectIdTransactionInit(OS_LOCK_MODE_GLOBAL, LOCAL_OBJID_TYPE, &token);

    /* Log result of acquiring transaction */
    {
        FILE *logf = fopen("/tmp/logs/353.txt", "a");
        if (logf != NULL)
        {
            fprintf(logf, "OS_ObjectIdTransactionInit returned %d, token_addr=%p\n", (int)return_code, (void *)&token);
            fclose(logf);
        }
    }

    if (return_code != OS_SUCCESS)
    {
        /* Log early return due to failure to initialize transaction */
        FILE *logf = fopen("/tmp/logs/353.txt", "a");
        if (logf != NULL)
        {
            fprintf(logf, "Early return from OS_SymbolTableDump due to OS_ObjectIdTransactionInit failure: %d\n", (int)return_code);
            fclose(logf);
        }
        return return_code;
    }

    /* Call the implementation and log inputs */
    {
        FILE *logf = fopen("/tmp/logs/353.txt", "a");
        if (logf != NULL)
        {
            fprintf(logf, "Calling OS_SymbolTableDump_Impl with path='%s', SizeLimit=%zu\n", translated_path, SizeLimit);
            fprintf(logf, "NOTE: downstream functions may call string-handling routines (e.g., OS_strnlen) using SizeLimit as a max length; this log records the maxlen passed for later reproduction of out-of-bounds reads.\n");
            fclose(logf);
        }
    }

    return_code = OS_SymbolTableDump_Impl(translated_path, SizeLimit);

    /* Log the return from impl and then cancel the transaction */
    {
        FILE *logf = fopen("/tmp/logs/353.txt", "a");
        if (logf != NULL)
        {
            fprintf(logf, "OS_SymbolTableDump_Impl returned %d\n", (int)return_code);
            fclose(logf);
        }
    }

    OS_ObjectIdTransactionCancel(&token);

    /* Final log and return */
    {
        FILE *logf = fopen("/tmp/logs/353.txt", "a");
        if (logf != NULL)
        {
            fprintf(logf, "EXIT OS_SymbolTableDump: return_code=%d\n", (int)return_code);
            fclose(logf);
        }
    }

    return return_code;
}
