int32 OS_SymbolTableDump(const char *filename, size_t SizeLimit)
{
    int32             return_code;
    char              translated_path[OS_MAX_LOCAL_PATH_LEN];
    OS_object_token_t token;

    /* Open log file for tracing control flow and state for repro */
    FILE *log = fopen("/tmp/logs/353.txt", "a");
    if (log != NULL)
    {
        fprintf(log, "OS_SymbolTableDump ENTER: filename_ptr=%p SizeLimit=%zu\n", (const void *)filename, SizeLimit);
        fflush(log);
    }

    /* Check parameters */
    OS_CHECK_POINTER(filename);

    if (log != NULL)
    {
        fprintf(log, "OS_SymbolTableDump: after OS_CHECK_POINTER, filename_ptr=%p\n", (const void *)filename);
        fflush(log);
    }

    /*
     ** Translate the filename to the Host System
     */
    return_code = OS_TranslatePath(filename, translated_path);

    if (log != NULL)
    {
        fprintf(log, "OS_SymbolTableDump: OS_TranslatePath returned %d\n", (int)return_code);
        if (return_code == OS_SUCCESS)
        {
            /* translated_path is valid only on success; log it for repro purposes */
            fprintf(log, "OS_SymbolTableDump: translated_path=\"%s\"\n", translated_path);
        }
        fflush(log);
    }

    if (return_code != OS_SUCCESS)
    {
        if (log != NULL)
        {
            fprintf(log, "OS_SymbolTableDump EXIT early: return_code=%d\n", (int)return_code);
            fclose(log);
        }
        return return_code;
    }

    /*
     * Locking the global ensures only one symbol table dump
     * can be executing.  It also prevents module loading/unloading
     * while the dump is occurring.
     *
     * Because calls to this function are serialized, the
     * underlying implementation may safely use globals for
     * state storage.
     */
    return_code = OS_ObjectIdTransactionInit(OS_LOCK_MODE_GLOBAL, LOCAL_OBJID_TYPE, &token);

    if (log != NULL)
    {
        fprintf(log, "OS_SymbolTableDump: OS_ObjectIdTransactionInit returned %d, token_addr=%p\n", (int)return_code, (void *)&token);
        fflush(log);
    }

    if (return_code != OS_SUCCESS)
    {
        if (log != NULL)
        {
            fprintf(log, "OS_SymbolTableDump EXIT after transaction init failure: return_code=%d\n", (int)return_code);
            fclose(log);
        }
        return return_code;
    }

    if (log != NULL)
    {
        fprintf(log, "OS_SymbolTableDump: calling OS_SymbolTableDump_Impl(translated_path, SizeLimit)\n");
        fflush(log);
    }

    return_code = OS_SymbolTableDump_Impl(translated_path, SizeLimit);

    if (log != NULL)
    {
        fprintf(log, "OS_SymbolTableDump: OS_SymbolTableDump_Impl returned %d\n", (int)return_code);
        fflush(log);
    }

    OS_ObjectIdTransactionCancel(&token);

    if (log != NULL)
    {
        fprintf(log, "OS_SymbolTableDump: OS_ObjectIdTransactionCancel called, final return_code=%d\n", (int)return_code);
        fclose(log);
    }

    return return_code;
}
