int32 OS_SymbolTableDump(const char *filename, size_t SizeLimit)
{
    int32             return_code;
    char              translated_path[OS_MAX_LOCAL_PATH_LEN];
    OS_object_token_t token;
    int               token_inited = 0;

    /* Open log file for probes - append so multiple runs accumulate evidence */
    FILE *log = fopen("/tmp/logs/355.txt", "a");
    if (log != NULL)
    {
        fprintf(log, "--- OS_SymbolTableDump ENTER ---\n");
        fprintf(log, "filename ptr=%p\n", (const void *)filename);
        if (filename != NULL)
        {
            /* log up to first 128 characters of the filename to avoid huge output */
            char tmp[129];
            size_t i;
            for (i = 0; i < sizeof(tmp) - 1 && filename[i] != '\0'; ++i)
            {
                tmp[i] = filename[i];
            }
            tmp[i] = '\0';
            fprintf(log, "filename[0..127] = '%s'\n", tmp);
        }
        fflush(log);
    }

    /* Check parameters */
    if (log != NULL) fprintf(log, "Calling OS_CHECK_POINTER(filename)\n"), fflush(log);
    OS_CHECK_POINTER(filename);

    /*
     ** Translate the filename to the Host System
     */
    if (log != NULL) fprintf(log, "Calling OS_TranslatePath(...)\n"), fflush(log);
    return_code = OS_TranslatePath(filename, translated_path);
    if (log != NULL) fprintf(log, "OS_TranslatePath returned %d\n", (int)return_code), fflush(log);
    if (return_code != OS_SUCCESS)
    {
        if (log != NULL)
        {
            fprintf(log, "Early return due to OS_TranslatePath failure: %d\n", (int)return_code);
            fprintf(log, "--- OS_SymbolTableDump EXIT (early) ---\n\n");
            fclose(log);
        }
        return return_code;
    }

    if (log != NULL)
    {
        fprintf(log, "translated_path = '%s'\n", translated_path);
        fflush(log);
    }

    /*
     * Locking the global ensures only one symbol table dump
     * can be executing.  It also prevents module loading/unloading
     * while the dump is occurring.
     *
     * Because calls to this function are serialized, the
     * underlying implementation may safely use globals for
     * state storage.
     */
    if (log != NULL) fprintf(log, "Calling OS_ObjectIdTransactionInit(...)\n"), fflush(log);
    return_code = OS_ObjectIdTransactionInit(OS_LOCK_MODE_GLOBAL, LOCAL_OBJID_TYPE, &token);
    if (log != NULL) fprintf(log, "OS_ObjectIdTransactionInit returned %d\n", (int)return_code), fflush(log);
    if (return_code != OS_SUCCESS)
    {
        if (log != NULL)
        {
            fprintf(log, "Could not initialize transaction, returning %d\n", (int)return_code);
            fprintf(log, "--- OS_SymbolTableDump EXIT (init fail) ---\n\n");
            fclose(log);
        }
        return return_code;
    }
    token_inited = 1;

    if (log != NULL)
    {
        /* Dump a small hex preview of the token to help correlate later calls */
        unsigned char *tb = (unsigned char *)&token;
        size_t tbsz = sizeof(token);
        size_t lim = (tbsz > 32U) ? 32U : tbsz;
        fprintf(log, "token (first %zu bytes):", (size_t)lim);
        for (size_t i = 0; i < lim; ++i)
        {
            fprintf(log, " %02X", (unsigned)tb[i]);
        }
        fprintf(log, "\n");
        fflush(log);
    }

    if (log != NULL) fprintf(log, "Calling OS_SymbolTableDump_Impl('%s', %zu)\n", translated_path, SizeLimit), fflush(log);
    return_code = OS_SymbolTableDump_Impl(translated_path, SizeLimit);
    if (log != NULL) fprintf(log, "OS_SymbolTableDump_Impl returned %d\n", (int)return_code), fflush(log);

    /* Ensure the transaction is canceled as in the original implementation */
    if (log != NULL) fprintf(log, "Calling OS_ObjectIdTransactionCancel(&token)\n"), fflush(log);
    OS_ObjectIdTransactionCancel(&token);
    if (log != NULL) fprintf(log, "OS_ObjectIdTransactionCancel done\n"), fflush(log);

    if (log != NULL)
    {
        fprintf(log, "Returning %d from OS_SymbolTableDump\n", (int)return_code);
        fprintf(log, "--- OS_SymbolTableDump EXIT ---\n\n");
        fclose(log);
    }

    return return_code;
}
