int32 OS_SymbolTableDump(const char *filename, size_t SizeLimit)
{
    int32             return_code;
    char              translated_path[OS_MAX_LOCAL_PATH_LEN];
    OS_object_token_t token;

    /* Open log for tracing control flow/state for MISRA rule investigation */
    FILE *log = fopen("/tmp/logs/355.txt", "a");
    if (log != NULL)
    {
        fprintf(log, "ENTER: OS_SymbolTableDump filename=%p name=\"%s\" SizeLimit=%zu\n",
                (const void *)filename,
                (filename != NULL) ? filename : "(null)",
                SizeLimit);
        fflush(log);
    }

    /* Check parameters */
    OS_CHECK_POINTER(filename);

    /*
     ** Translate the filename to the Host System
     */
    return_code = OS_TranslatePath(filename, translated_path);
    if (log != NULL)
    {
        fprintf(log, "OS_TranslatePath returned %d\n", (int)return_code);
        fflush(log);
    }
    if (return_code != OS_SUCCESS)
    {
        if (log != NULL)
        {
            fprintf(log, "EXIT: TranslatePath failed return_code=%d\n", (int)return_code);
            fclose(log);
        }
        return return_code;
    }

    if (log != NULL)
    {
        /* Only print translated_path when translation succeeded to avoid reading uninitialized data */
        fprintf(log, "translated_path addr=%p \"%s\"\n", (const void *)translated_path, translated_path);
        fflush(log);
    }

    /*
     * Locking the global ensures only one symbol table dump
     * can be executing.  It also prevents module loading/unloading
     * while the dump is occurring.
     *
     * Because calls to this function are serialized, the
     * underlying implementation may safely use globals for
     * state storage.
     */
    return_code = OS_ObjectIdTransactionInit(OS_LOCK_MODE_GLOBAL, LOCAL_OBJID_TYPE, &token);
    if (log != NULL)
    {
        fprintf(log, "OS_ObjectIdTransactionInit returned %d\n", (int)return_code);
        fflush(log);
    }
    if (return_code != OS_SUCCESS)
    {
        if (log != NULL)
        {
            fprintf(log, "EXIT: TransactionInit failed return_code=%d\n", (int)return_code);
            fclose(log);
        }
        return return_code;
    }

    if (log != NULL)
    {
        /* Log the call site and arguments passed to the implementation.
         * This helps show whether a non-const char * might be accepted
         * by the implementation even though the original parameter
         * was const-qualified. */
        fprintf(log, "Calling OS_SymbolTableDump_Impl with translated_path addr=%p \"%s\" SizeLimit=%zu\n",
                (const void *)translated_path, translated_path, SizeLimit);
        fflush(log);
    }

    return_code = OS_SymbolTableDump_Impl(translated_path, SizeLimit);

    if (log != NULL)
    {
        fprintf(log, "OS_SymbolTableDump_Impl returned %d\n", (int)return_code);
        fflush(log);
    }

    OS_ObjectIdTransactionCancel(&token);

    if (log != NULL)
    {
        fprintf(log, "Transaction canceled. Final return_code=%d\n", (int)return_code);
        fclose(log);
    }

    return return_code;
}
