int32 OS_TaskDelete(osal_id_t task_id)
{
    int32                      return_code;
    OS_object_token_t          token;
    OS_task_internal_record_t *task;
    osal_task_entry            delete_hook;
    FILE *logfp = NULL;

    /* Open log file for append; best-effort logging for reproduction */
    logfp = fopen("/tmp/logs/775.txt", "a");
    if (logfp != NULL)
    {
        fprintf(logfp, "OS_TaskDelete entered: task_id=%llu\n", (unsigned long long)task_id);
    }

    delete_hook = NULL;
    return_code = OS_ObjectIdGetById(OS_LOCK_MODE_EXCLUSIVE, LOCAL_OBJID_TYPE, task_id, &token);

    if (logfp != NULL)
    {
        fprintf(logfp, "OS_ObjectIdGetById returned: %d, token_addr=%p\n",
                (int)return_code, (void *)&token);
    }

    if (return_code == OS_SUCCESS)
    {
        task = OS_OBJECT_TABLE_GET(OS_task_table, token);

        if (logfp != NULL)
        {
            fprintf(logfp, "Resolved task pointer: %p\n", (void *)task);
            /* Attempt to log the delete hook field value (function pointer) */
            fprintf(logfp, "task->delete_hook_pointer (raw): %p\n",
                    (void *)task->delete_hook_pointer);
        }

        /* Save the delete hook, as we do not want to call it while locked */
        delete_hook = task->delete_hook_pointer;

        if (logfp != NULL)
        {
            fprintf(logfp, "Saved delete_hook=%p, about to call OS_TaskDelete_Impl(&token)\n",
                    (void *)delete_hook);
            fflush(logfp);
        }

        return_code = OS_TaskDelete_Impl(&token);

        if (logfp != NULL)
        {
            fprintf(logfp, "OS_TaskDelete_Impl returned: %d\n", (int)return_code);
        }

        /* Complete the operation via the common routine */
        return_code = OS_ObjectIdFinalizeDelete(return_code, &token);

        if (logfp != NULL)
        {
            fprintf(logfp, "OS_ObjectIdFinalizeDelete completed: %d\n", (int)return_code);
        }
    }

    /*
    ** Call the thread Delete hook if there is one.
    */
    if (return_code == OS_SUCCESS && delete_hook != NULL)
    {
        if (logfp != NULL)
        {
            fprintf(logfp, "Calling delete_hook at %p\n", (void *)delete_hook);
            fflush(logfp);
        }

        delete_hook();

        if (logfp != NULL)
        {
            fprintf(logfp, "delete_hook returned\n");
        }
    }

    if (logfp != NULL)
    {
        fprintf(logfp, "OS_TaskDelete exiting: return_code=%d\n", (int)return_code);
        fclose(logfp);
    }

    return return_code;
}
