void OS_TaskEntryPoint(osal_id_t task_id)
{
    osal_task_entry task_entry;
    FILE *log = NULL;

    /* Try to open the log file. Do not fail if logging is not available. */
    log = fopen("/tmp/logs/100.txt", "a");
    if (log != NULL)
    {
        fprintf(log, "OS_TaskEntryPoint: ENTER task_id=%lu\n", (unsigned long)task_id);
        fflush(log);
    }

    if (OS_TaskPrepare(task_id, &task_entry) == OS_SUCCESS)
    {
        if (log != NULL)
        {
            fprintf(log, "OS_TaskEntryPoint: OS_TaskPrepare returned OS_SUCCESS\n");
            /* Log whether we got a non-NULL entry and perform a pointer-equality check
               against this entry point function to capture direct-recursion cases. */
            fprintf(log, "OS_TaskEntryPoint: task_entry pointer is %p\n", (void *)task_entry);

            /* Compare the obtained entry to this function's address to detect a direct
               self-call registration. Cast is used to ensure the types are comparable
               for logging purposes only. */
            if (task_entry == (osal_task_entry)OS_TaskEntryPoint)
            {
                fprintf(log, "OS_TaskEntryPoint: WARNING: task_entry == OS_TaskEntryPoint (direct recursion possible)\n");
            }

            fflush(log);
        }

        if (task_entry != NULL)
        {
            if (log != NULL)
            {
                fprintf(log, "OS_TaskEntryPoint: invoking task_entry() at %p\n", (void *)task_entry);
                fflush(log);
            }

            /* Call the task entry (preserve original behavior). */
            (*task_entry)();

            if (log != NULL)
            {
                fprintf(log, "OS_TaskEntryPoint: task_entry() returned\n");
                fflush(log);
            }
        }
        else
        {
            if (log != NULL)
            {
                fprintf(log, "OS_TaskEntryPoint: task_entry is NULL, skipping call\n");
                fflush(log);
            }
        }
    }
    else
    {
        if (log != NULL)
        {
            fprintf(log, "OS_TaskEntryPoint: OS_TaskPrepare did not return OS_SUCCESS\n");
            fflush(log);
        }
    }

    if (log != NULL)
    {
        fprintf(log, "OS_TaskEntryPoint: calling OS_TaskExit()\n");
        fclose(log);
    }

    /* If the function returns, treat as a normal exit and do the proper cleanup */
    OS_TaskExit();
}
