int32 OS_TaskFindIdBySystemData(osal_id_t *task_id, const void *sysdata, size_t sysdata_size)
{
    int32             return_code;
    OS_object_token_t token;
    FILE *log = fopen("/tmp/logs/1325.txt", "a");

    /* Minimal entry probe so the function being reached is logged */
    if (log != NULL)
    {
        fprintf(log, "ENTER: OS_TaskFindIdBySystemData(task_id=%p, sysdata=%p, sysdata_size=%zu)\n",
                (void *)task_id, sysdata, sysdata_size);
        fflush(log);
    }

    /* Check parameters */
    OS_CHECK_POINTER(task_id);

    /* The "sysdata" and "sysdata_size" must be passed to the underlying impl for validation */
    return_code = OS_TaskValidateSystemData_Impl(sysdata, sysdata_size);
    if (log != NULL)
    {
        fprintf(log, "AFTER: OS_TaskValidateSystemData_Impl returned %ld\n", (long)return_code);
        fflush(log);
    }

    if (return_code != OS_SUCCESS)
    {
        if (log != NULL)
        {
            fprintf(log, "EXIT EARLY: validation failed, return %ld\n", (long)return_code);
            fclose(log);
        }
        return return_code;
    }

    /* Log the fact that the code will perform a const-discarding cast when invoking the search */
    if (log != NULL)
    {
        fprintf(log, "ABOUT TO CALL: OS_ObjectIdGetBySearch(..., (void *)sysdata /* const->non-const cast */, sysdata=%p)\n",
                (void *)sysdata);
        fflush(log);
    }

    /* The cast here is the point of interest for the MISRA 8.13 scenario (const discarded) */
    return_code = OS_ObjectIdGetBySearch(OS_LOCK_MODE_GLOBAL, LOCAL_OBJID_TYPE, OS_TaskIdMatchSystemData_Impl,
                                         (void *)sysdata, &token);

    if (log != NULL)
    {
        fprintf(log, "AFTER: OS_ObjectIdGetBySearch returned %ld\n", (long)return_code);
        fflush(log);
    }

    if (return_code == OS_SUCCESS)
    {
        *task_id = OS_ObjectIdFromToken(&token);

        if (log != NULL)
        {
            /* Attempt to log the derived task id and token address for correlation */
            fprintf(log, "MATCH FOUND: task_id=%lu (from token at %p)\n", (unsigned long)(*task_id), (void *)&token);
            fflush(log);
        }

        OS_ObjectIdRelease(&token);
    }

    if (log != NULL)
    {
        fprintf(log, "EXIT: OS_TaskFindIdBySystemData returning %ld\n", (long)return_code);
        fclose(log);
    }

    return return_code;
}
