int32 OS_TaskFindIdBySystemData(osal_id_t *task_id, const void *sysdata, size_t sysdata_size)
{
    int32             return_code;
    OS_object_token_t token;

    /* Minimal entry log */
    {
        FILE *log = fopen("/tmp/logs/1325.txt", "a");
        if (log != NULL)
        {
            fprintf(log, "ENTER: OS_TaskFindIdBySystemData task_id=%p sysdata=%p sysdata_size=%zu\n",
                    (void *)task_id, sysdata, sysdata_size);
            /* If there is some system data, print a short hex preview (up to 64 bytes) */
            if (sysdata != NULL && sysdata_size > 0)
            {
                const unsigned char *b = (const unsigned char *)sysdata;
                size_t max_print = sysdata_size > 64 ? 64 : sysdata_size;
                fprintf(log, "sysdata (first %zu bytes):", max_print);
                for (size_t i = 0; i < max_print; ++i)
                {
                    fprintf(log, " %02X", (unsigned) b[i]);
                }
                if (sysdata_size > max_print)
                {
                    fprintf(log, " ... (total %zu bytes)", sysdata_size);
                }
                fprintf(log, "\n");
            }

            fclose(log);
        }
    }

    /* Check parameters */
    OS_CHECK_POINTER(task_id);

    /* The "sysdata" and "sysdata_size" must be passed to the underlying impl for validation */
    return_code = OS_TaskValidateSystemData_Impl(sysdata, sysdata_size);

    /* Log the result of validation */
    {
        FILE *log = fopen("/tmp/logs/1325.txt", "a");
        if (log != NULL)
        {
            fprintf(log, "After OS_TaskValidateSystemData_Impl: return_code=%d\n", (int)return_code);
            fclose(log);
        }
    }

    if (return_code != OS_SUCCESS)
    {
        /* Log early return */
        FILE *log = fopen("/tmp/logs/1325.txt", "a");
        if (log != NULL)
        {
            fprintf(log, "EXIT (validate failed): return_code=%d\n", (int)return_code);
            fclose(log);
        }
        return return_code;
    }

    /* Log the upcoming search call and the cast that demonstrates the non-const passing */
    {
        FILE *log = fopen("/tmp/logs/1325.txt", "a");
        if (log != NULL)
        {
            fprintf(log, "Calling OS_ObjectIdGetBySearch with ref=(void*)sysdata -> %p (note: original sysdata is const)\n",
                    (void *)sysdata);
            fclose(log);
        }
    }

    return_code = OS_ObjectIdGetBySearch(OS_LOCK_MODE_GLOBAL, LOCAL_OBJID_TYPE, OS_TaskIdMatchSystemData_Impl,
                                         (void *)sysdata, &token);

    /* Log the result of the search */
    {
        FILE *log = fopen("/tmp/logs/1325.txt", "a");
        if (log != NULL)
        {
            fprintf(log, "After OS_ObjectIdGetBySearch: return_code=%d\n", (int)return_code);
            if (return_code == OS_SUCCESS)
            {
                unsigned long long assigned_id = (unsigned long long)(*task_id); /* will be set below */
                fprintf(log, "OS_ObjectIdGetBySearch indicated success; token_addr=%p (token opaque), will extract id\n",
                        (void *)&token);
                /* The assignment happens below in original code; we log after performing it to capture the value. */
            }
            fclose(log);
        }
    }

    if (return_code == OS_SUCCESS)
    {
        *task_id = OS_ObjectIdFromToken(&token);

        /* Log the assigned task id and token release */
        FILE *log = fopen("/tmp/logs/1325.txt", "a");
        if (log != NULL)
        {
            fprintf(log, "Assigned *task_id=%llu from token=%p\n", (unsigned long long)(*task_id), (void *)&token);
            fprintf(log, "Releasing token and exiting with success\n");
            fclose(log);
        }

        OS_ObjectIdRelease(&token);
    }

    /* Final exit log */
    {
        FILE *log = fopen("/tmp/logs/1325.txt", "a");
        if (log != NULL)
        {
            fprintf(log, "EXIT: OS_TaskFindIdBySystemData return_code=%d\n", (int)return_code);
            fclose(log);
        }
    }

    return return_code;
}
