int32 OS_TaskGetInfo(osal_id_t task_id, OS_task_prop_t *task_prop)
{
    OS_common_record_t *       record;
    int32                      return_code;
    OS_object_token_t          token;
    OS_task_internal_record_t *task;
    /* File handle for logging. Open in append mode so multiple calls accumulate. */
    FILE *logf = NULL;

    /* Check parameters */
    OS_CHECK_POINTER(task_prop);

    /* Minimal probe: log entry to function and the task_id argument. */
    logf = fopen("/tmp/logs/1488.txt", "a");
    if (logf != NULL)
    {
        fprintf(logf, "OS_TaskGetInfo ENTER: task_id=%lu\n", (unsigned long)task_id);
        fflush(logf);
    }

    memset(task_prop, 0, sizeof(OS_task_prop_t));

    return_code = OS_ObjectIdGetById(OS_LOCK_MODE_GLOBAL, LOCAL_OBJID_TYPE, task_id, &token);

    if (logf != NULL)
    {
        fprintf(logf, "OS_TaskGetInfo: OS_ObjectIdGetById returned %ld\n", (long)return_code);
        fflush(logf);
    }

    if (return_code == OS_SUCCESS)
    {
        record = OS_OBJECT_TABLE_GET(OS_global_task_table, token);
        task   = OS_OBJECT_TABLE_GET(OS_task_table, token);

        if (logf != NULL)
        {
            fprintf(logf, "OS_TaskGetInfo: record=%p, task=%p\n", (void *)record, (void *)task);
            if (record != NULL)
            {
                fprintf(logf, "  record->creator=%lu\n", (unsigned long)record->creator);
                fprintf(logf, "  record->name_entry=%p\n", (void *)record->name_entry);
            }
            if (task != NULL)
            {
                fprintf(logf, "  task->stack_size=%lu, task->priority=%lu\n",
                        (unsigned long)task->stack_size, (unsigned long)task->priority);
            }
            fflush(logf);
        }

        if (record->name_entry != NULL)
        {
            strncpy(task_prop->name, record->name_entry, sizeof(task_prop->name) - 1);
            task_prop->name[sizeof(task_prop->name) - 1] = 0;
        }
        task_prop->creator    = record->creator;
        task_prop->stack_size = task->stack_size;
        task_prop->priority   = task->priority;

        if (logf != NULL)
        {
            fprintf(logf, "OS_TaskGetInfo: task_prop BEFORE Impl call: name=\"%s\", creator=%lu, stack_size=%lu, priority=%lu\n",
                    task_prop->name ? task_prop->name : "(null)",
                    (unsigned long)task_prop->creator,
                    (unsigned long)task_prop->stack_size,
                    (unsigned long)task_prop->priority);
            fflush(logf);
        }

        /* Call the platform-specific implementation. We log before and after to capture any side-effects. */
        return_code = OS_TaskGetInfo_Impl(&token, task_prop);

        if (logf != NULL)
        {
            fprintf(logf, "OS_TaskGetInfo: OS_TaskGetInfo_Impl returned %ld\n", (long)return_code);
            fprintf(logf, "OS_TaskGetInfo: task_prop AFTER Impl call: name=\"%s\", creator=%lu, stack_size=%lu, priority=%lu\n",
                    task_prop->name ? task_prop->name : "(null)",
                    (unsigned long)task_prop->creator,
                    (unsigned long)task_prop->stack_size,
                    (unsigned long)task_prop->priority);
            fflush(logf);
        }

        OS_ObjectIdRelease(&token);

        if (logf != NULL)
        {
            fprintf(logf, "OS_TaskGetInfo: token released, final return_code=%ld\n", (long)return_code);
            fprintf(logf, "OS_TaskGetInfo EXIT\n\n");
            fclose(logf);
            logf = NULL;
        }
    }
    else
    {
        if (logf != NULL)
        {
            fprintf(logf, "OS_TaskGetInfo: failed to get object id, returning %ld\n", (long)return_code);
            fprintf(logf, "OS_TaskGetInfo EXIT\n\n");
            fclose(logf);
            logf = NULL;
        }
    }

    return return_code;
}
