int32 OS_TaskGetInfo(osal_id_t task_id, OS_task_prop_t *task_prop)
{
    OS_common_record_t *       record;
    int32                      return_code;
    OS_object_token_t          token;
    OS_task_internal_record_t *task;
    FILE *log = NULL;

    /* Try to open the log file for append. If this fails, continue silently. */
    log = fopen("/tmp/logs/1485.txt", "a");
    if (log != NULL)
    {
        fprintf(log, "ENTER: OS_TaskGetInfo(task_id=%llu, task_prop=%p)\n", (unsigned long long)task_id, (void *)task_prop);
        fflush(log);
    }

    /* Check parameters */
    OS_CHECK_POINTER(task_prop);

    memset(task_prop, 0, sizeof(OS_task_prop_t));
    if (log != NULL)
    {
        fprintf(log, "INFO: task_prop zeroed at %p (size=%zu)\n", (void *)task_prop, sizeof(OS_task_prop_t));
        fflush(log);
    }

    return_code = OS_ObjectIdGetById(OS_LOCK_MODE_GLOBAL, LOCAL_OBJID_TYPE, task_id, &token);
    if (log != NULL)
    {
        fprintf(log, "INFO: OS_ObjectIdGetById returned %ld\n", (long)return_code);
        fflush(log);
    }

    if (return_code == OS_SUCCESS)
    {
        record = OS_OBJECT_TABLE_GET(OS_global_task_table, token);
        task   = OS_OBJECT_TABLE_GET(OS_task_table, token);

        if (log != NULL)
        {
            fprintf(log, "INFO: record=%p, task=%p, token_addr=%p\n", (void *)record, (void *)task, (void *)&token);
            /* Attempt to log the name_entry pointer and content if present */
            if (record != NULL)
            {
                fprintf(log, "INFO: record->name_entry=%p\n", (void *)record->name_entry);
                if (record->name_entry != NULL)
                {
                    /* Print as a string; mirror how the code uses it (read-only) */
                    fprintf(log, "INFO: record->name_entry content=\"%s\"\n", record->name_entry);
                }
                else
                {
                    fprintf(log, "INFO: record->name_entry is NULL\n");
                }

                /* Log creator field (best-effort as integer) */
                fprintf(log, "INFO: record->creator=%lld\n", (long long)record->creator);
            }
            else
            {
                fprintf(log, "WARN: record is NULL\n");
            }

            if (task != NULL)
            {
                fprintf(log, "INFO: task->stack_size=%lld, task->priority=%lld\n", (long long)task->stack_size, (long long)task->priority);
            }
            else
            {
                fprintf(log, "WARN: task is NULL\n");
            }
            fflush(log);
        }

        if (record->name_entry != NULL)
        {
            strncpy(task_prop->name, record->name_entry, sizeof(task_prop->name) - 1);
            task_prop->name[sizeof(task_prop->name) - 1] = 0;
            if (log != NULL)
            {
                fprintf(log, "ACTION: copied name_entry to task_prop->name: \"%s\"\n", task_prop->name);
                fflush(log);
            }
        }
        task_prop->creator    = record->creator;
        task_prop->stack_size = task->stack_size;
        task_prop->priority   = task->priority;

        if (log != NULL)
        {
            fprintf(log, "INFO: before OS_TaskGetInfo_Impl: task_prop->name=\"%s\", creator=%lld, stack_size=%lld, priority=%lld\n",
                    task_prop->name, (long long)task_prop->creator, (long long)task_prop->stack_size, (long long)task_prop->priority);
            fflush(log);
        }

        return_code = OS_TaskGetInfo_Impl(&token, task_prop);

        if (log != NULL)
        {
            fprintf(log, "INFO: OS_TaskGetInfo_Impl returned %ld\n", (long)return_code);
            fflush(log);
        }

        OS_ObjectIdRelease(&token);

        if (log != NULL)
        {
            fprintf(log, "INFO: Released token for task_id=%llu\n", (unsigned long long)task_id);
            fflush(log);
        }
    }

    if (log != NULL)
    {
        fprintf(log, "EXIT: OS_TaskGetInfo(task_id=%llu) => return_code=%ld\n\n", (unsigned long long)task_id, (long)return_code);
        fclose(log);
    }

    return return_code;
}
