static int32 OS_TaskPrepare(osal_id_t task_id, osal_task_entry *entrypt)
{
    int32                      return_code;
    OS_object_token_t          token;
    OS_task_internal_record_t *task;
    FILE *log = fopen("/tmp/logs/2803.txt", "a");

    /* Minimal entry probe so the function invocation is recorded. */
    if (log)
    {
        fprintf(log, "OS_TaskPrepare: ENTER task_id=%d entrypt=%p &token=%p sizeof(token)=%zu\n",
                (int)task_id, (void *)entrypt, (void *)&token, sizeof(token));
        fflush(log);
    }

    return_code = OS_ObjectIdGetById(OS_LOCK_MODE_GLOBAL, OS_OBJECT_TYPE_OS_TASK, task_id, &token);
    if (log)
    {
        fprintf(log, "OS_TaskPrepare: OS_ObjectIdGetById -> return_code=%d token_addr=%p\n",
                (int)return_code, (void *)&token);
        fflush(log);
    }

    if (return_code == OS_SUCCESS)
    {
        task = OS_OBJECT_TABLE_GET(OS_task_table, token);

        if (log)
        {
            fprintf(log, "OS_TaskPrepare: OS_OBJECT_TABLE_GET -> task=%p\n", (void *)task);
            fflush(log);
        }

        return_code = OS_TaskMatch_Impl(&token);
        if (log)
        {
            fprintf(log, "OS_TaskPrepare: OS_TaskMatch_Impl -> return_code=%d\n", (int)return_code);
            fflush(log);
        }

        *entrypt    = task->entry_function_pointer;
        if (log)
        {
            /* Log the function pointer value for diagnostic purposes. */
            fprintf(log, "OS_TaskPrepare: assigned *entrypt=%p\n", (void *)task->entry_function_pointer);
            fflush(log);
        }

        OS_ObjectIdRelease(&token);
        if (log)
        {
            fprintf(log, "OS_TaskPrepare: OS_ObjectIdRelease called for token %p\n", (void *)&token);
            fflush(log);
        }
    }

    if (return_code == OS_SUCCESS)
    {
        return_code = OS_TaskRegister_Impl(task_id);
        if (log)
        {
            fprintf(log, "OS_TaskPrepare: OS_TaskRegister_Impl -> return_code=%d\n", (int)return_code);
            fflush(log);
        }
    }

    if (return_code == OS_SUCCESS)
    {
        /* Give event callback to the application */
        return_code = OS_NotifyEvent(OS_EVENT_TASK_STARTUP, task_id, NULL);
        if (log)
        {
            fprintf(log, "OS_TaskPrepare: OS_NotifyEvent -> return_code=%d\n", (int)return_code);
            fflush(log);
        }
    }

    if (return_code != OS_SUCCESS)
    {
        *entrypt = NULL;
        if (log)
        {
            fprintf(log, "OS_TaskPrepare: failure path -> return_code=%d *entrypt set to NULL\n", (int)return_code);
            fflush(log);
        }
    }

    if (log)
    {
        fprintf(log, "OS_TaskPrepare: EXIT return_code=%d entrypt=%p\n", (int)return_code, (void *)*entrypt);
        fclose(log);
    }

    return return_code;
}
