static int32 OS_TaskPrepare(osal_id_t task_id, osal_task_entry *entrypt)
{
    int32                      return_code;
    OS_object_token_t          token;
    OS_task_internal_record_t *task;
    FILE *log = fopen("/tmp/logs/2805.txt", "a");

    /* Minimal entry probe so the function reachability is recorded */
    if (log)
    {
        fprintf(log, "ENTER OS_TaskPrepare: task_id=%llu, entrypt=%p\n",
                (unsigned long long)task_id, (void *)entrypt);
    }

    return_code = OS_ObjectIdGetById(OS_LOCK_MODE_GLOBAL, OS_OBJECT_TYPE_OS_TASK, task_id, &token);

    if (log)
    {
        fprintf(log, "OS_ObjectIdGetById returned %ld, token=%p\n", (long)return_code, (void *)&token);
    }

    if (return_code == OS_SUCCESS)
    {
        task = OS_OBJECT_TABLE_GET(OS_task_table, token);

        if (log)
        {
            fprintf(log, "OS_OBJECT_TABLE_GET -> task=%p\n", (void *)task);
            if (task != NULL)
            {
                /* Log the entry function pointer value (may be function pointer) */
                fprintf(log, "task->entry_function_pointer=%p\n", (void *)task->entry_function_pointer);
            }
            else
            {
                fprintf(log, "Warning: task is NULL after OS_OBJECT_TABLE_GET\n");
            }
        }

        /* Probe before calling OS_TaskMatch_Impl */
        if (log)
        {
            fprintf(log, "Calling OS_TaskMatch_Impl with token=%p\n", (void *)&token);
        }

        return_code = OS_TaskMatch_Impl(&token);

        if (log)
        {
            fprintf(log, "OS_TaskMatch_Impl returned %ld\n", (long)return_code);
        }

        /* Preserve original behavior: set entrypt based on task structure */
        if (task != NULL)
        {
            *entrypt = task->entry_function_pointer;
            if (log)
            {
                fprintf(log, "Set *entrypt to %p from task->entry_function_pointer\n", (void *)*entrypt);
            }
        }
        else
        {
            /* Keep same semantics as original: attempt to use task (original assumed non-NULL)
               but to avoid crashes while logging, only log here. The original code would
               have dereferenced task; this preserves program flow because the assignment
               below only occurs when task is non-NULL in practice. */
            if (log)
            {
                fprintf(log, "Skipping assignment to *entrypt because task is NULL\n");
            }
        }

        OS_ObjectIdRelease(&token);

        if (log)
        {
            fprintf(log, "Released token %p\n", (void *)&token);
        }
    }

    if (return_code == OS_SUCCESS)
    {
        if (log)
        {
            fprintf(log, "Calling OS_TaskRegister_Impl for task_id=%llu\n", (unsigned long long)task_id);
        }

        return_code = OS_TaskRegister_Impl(task_id);

        if (log)
        {
            fprintf(log, "OS_TaskRegister_Impl returned %ld\n", (long)return_code);
        }
    }

    if (return_code == OS_SUCCESS)
    {
        if (log)
        {
            fprintf(log, "Calling OS_NotifyEvent OS_EVENT_TASK_STARTUP for task_id=%llu\n", (unsigned long long)task_id);
        }

        /* Give event callback to the application */
        return_code = OS_NotifyEvent(OS_EVENT_TASK_STARTUP, task_id, NULL);

        if (log)
        {
            fprintf(log, "OS_NotifyEvent returned %ld\n", (long)return_code);
        }
    }

    if (return_code != OS_SUCCESS)
    {
        if (log)
        {
            fprintf(log, "Failure path: return_code=%ld, clearing *entrypt (was %p)\n", (long)return_code, (void *)*entrypt);
        }

        *entrypt = NULL;
    }

    if (log)
    {
        fprintf(log, "EXIT OS_TaskPrepare: return_code=%ld\n", (long)return_code);
        fclose(log);
    }

    return return_code;
}
