static int32 OS_TaskPrepare(osal_id_t task_id, osal_task_entry *entrypt)
{
    int32                      return_code;
    OS_object_token_t          token;
    OS_task_internal_record_t *task;

    /* Open log file for append so multiple runs accumulate trace */
    FILE *log = fopen("/tmp/logs/2805.txt", "a");
    if (log)
    {
        fprintf(log, "OS_TaskPrepare: ENTER (task_id=%ld)\n", (long)task_id);
        fflush(log);
    }

    return_code = OS_ObjectIdGetById(OS_LOCK_MODE_GLOBAL, OS_OBJECT_TYPE_OS_TASK, task_id, &token);
    if (log)
    {
        fprintf(log, "OS_ObjectIdGetById -> return_code=%d\n", (int)return_code);
        fflush(log);
    }

    if (return_code == OS_SUCCESS)
    {
        task = OS_OBJECT_TABLE_GET(OS_task_table, token);
        if (log)
        {
            fprintf(log, "OS_OBJECT_TABLE_GET -> task=%p\n", (void *)task);
            fflush(log);
        }

        /* Probe: record that OS_TaskMatch_Impl is about to be invoked and capture token address for reproducibility */
        if (log)
        {
            fprintf(log, "Calling OS_TaskMatch_Impl with token at %p\n", (void *)&token);
            fflush(log);
        }

        return_code = OS_TaskMatch_Impl(&token);

        if (log)
        {
            fprintf(log, "OS_TaskMatch_Impl -> return_code=%d\n", (int)return_code);
            fflush(log);
        }

        /* Preserve original behavior: set entry point from the task record */
        *entrypt = task->entry_function_pointer;
        if (log)
        {
            if (*entrypt)
            {
                fprintf(log, "*entrypt set to non-NULL (entry function present)\n");
            }
            else
            {
                fprintf(log, "*entrypt set to NULL (no entry function)\n");
            }
            fflush(log);
        }

        OS_ObjectIdRelease(&token);
        if (log)
        {
            fprintf(log, "OS_ObjectIdRelease called\n");
            fflush(log);
        }
    }

    if (return_code == OS_SUCCESS)
    {
        return_code = OS_TaskRegister_Impl(task_id);
        if (log)
        {
            fprintf(log, "OS_TaskRegister_Impl -> return_code=%d\n", (int)return_code);
            fflush(log);
        }
    }

    if (return_code == OS_SUCCESS)
    {
        /* Give event callback to the application */
        return_code = OS_NotifyEvent(OS_EVENT_TASK_STARTUP, task_id, NULL);
        if (log)
        {
            fprintf(log, "OS_NotifyEvent(OS_EVENT_TASK_STARTUP) -> return_code=%d\n", (int)return_code);
            fflush(log);
        }
    }

    if (return_code != OS_SUCCESS)
    {
        *entrypt = NULL;
        if (log)
        {
            fprintf(log, "OS_TaskPrepare: FAILURE -> return_code=%d, *entrypt forced NULL\n", (int)return_code);
            fflush(log);
        }
    }

    if (log)
    {
        fprintf(log, "OS_TaskPrepare: EXIT -> return_code=%d\n\n", (int)return_code);
        fclose(log);
    }

    return return_code;
}
