static int32 OS_TaskPrepare(osal_id_t task_id, osal_task_entry *entrypt)
{
    int32                      return_code;
    OS_object_token_t          token;
    OS_task_internal_record_t *task;
    FILE *log = NULL;

    /* Open log file (try primary path, then fallback) */
    log = fopen("/tmp/logs/2805.txt", "a");
    if (log == NULL)
    {
        log = fopen("/tmp/2805.txt", "a");
    }

    if (log)
    {
        fprintf(log, "ENTER OS_TaskPrepare: task_id=%lld, entrypt=%p\n", (long long)task_id, (void *)entrypt);
        fflush(log);
    }

    return_code = OS_ObjectIdGetById(OS_LOCK_MODE_GLOBAL, OS_OBJECT_TYPE_OS_TASK, task_id, &token);

    if (log)
    {
        fprintf(log, "After OS_ObjectIdGetById: return_code=%lld, token_addr=%p\n", (long long)return_code, (void *)&token);
        fflush(log);
    }

    if (return_code == OS_SUCCESS)
    {
        task = OS_OBJECT_TABLE_GET(OS_task_table, token);

        return_code = OS_TaskMatch_Impl(&token);
        *entrypt    = task->entry_function_pointer;

        if (log)
        {
            fprintf(log, "Matched task: task_ptr=%p, entry_function_pointer=%p, return_code=%lld\n",
                    (void *)task, (void *)task->entry_function_pointer, (long long)return_code);
            fflush(log);
        }

        OS_ObjectIdRelease(&token);

        if (log)
        {
            fprintf(log, "Released token: token_addr=%p\n", (void *)&token);
            fflush(log);
        }
    }

    if (return_code == OS_SUCCESS)
    {
        return_code = OS_TaskRegister_Impl(task_id);
        if (log)
        {
            fprintf(log, "After OS_TaskRegister_Impl: return_code=%lld\n", (long long)return_code);
            fflush(log);
        }
    }

    if (return_code == OS_SUCCESS)
    {
        /* Give event callback to the application */
        if (log)
        {
            /* Log the event parameters and the data pointer being forwarded (NULL in this call) */
            fprintf(log, "Calling OS_NotifyEvent: event=%lld, object_id=%lld, data=%p\n",
                    (long long)OS_EVENT_TASK_STARTUP, (long long)task_id, (void *)NULL);
            fflush(log);
        }

        return_code = OS_NotifyEvent(OS_EVENT_TASK_STARTUP, task_id, NULL);

        if (log)
        {
            fprintf(log, "After OS_NotifyEvent: return_code=%lld\n", (long long)return_code);
            fflush(log);
        }
    }

    if (return_code != OS_SUCCESS)
    {
        if (log)
        {
            fprintf(log, "Error path: return_code=%lld - setting *entrypt = NULL\n", (long long)return_code);
            fflush(log);
        }

        *entrypt = NULL;
    }

    if (log)
    {
        fprintf(log, "EXIT OS_TaskPrepare: final_return_code=%lld, final_entrypt=%p\n", (long long)return_code, (void *)*entrypt);
        fclose(log);
    }

    return return_code;
}
