static int32 OS_TaskPrepare(osal_id_t task_id, osal_task_entry *entrypt)
{
    int32                      return_code;
    OS_object_token_t          token;
    OS_task_internal_record_t *task;
    FILE *log = NULL;

    /* Attempt to open log file for append; best-effort logging only */
    log = fopen("/tmp/logs/2805.txt", "a");
    if (log != NULL)
    {
        fprintf(log, "OS_TaskPrepare ENTER: task_id=%lu, entrypt=%p\n", (unsigned long)task_id, (void *)entrypt);
        fflush(log);
    }

    return_code = OS_ObjectIdGetById(OS_LOCK_MODE_GLOBAL, OS_OBJECT_TYPE_OS_TASK, task_id, &token);
    if (log != NULL) fprintf(log, "OS_ObjectIdGetById -> return_code=%ld\n", (long)return_code);

    if (return_code == OS_SUCCESS)
    {
        task = OS_OBJECT_TABLE_GET(OS_task_table, token);
        if (log != NULL) fprintf(log, "OS_OBJECT_TABLE_GET -> task=%p\n", (void *)task);

        return_code = OS_TaskMatch_Impl(&token);
        if (log != NULL) fprintf(log, "OS_TaskMatch_Impl -> return_code=%ld\n", (long)return_code);

        /* Preserve original behavior: capture the entry point from the task record */
        *entrypt = task->entry_function_pointer;
        if (log != NULL) fprintf(log, "Set *entrypt to task->entry_function_pointer=%p\n", (void *)task->entry_function_pointer);

        OS_ObjectIdRelease(&token);
        if (log != NULL) fprintf(log, "OS_ObjectIdRelease called\n");
    }

    if (return_code == OS_SUCCESS)
    {
        return_code = OS_TaskRegister_Impl(task_id);
        if (log != NULL) fprintf(log, "OS_TaskRegister_Impl -> return_code=%ld\n", (long)return_code);
    }

    if (return_code == OS_SUCCESS)
    {
        /* Give event callback to the application */
        return_code = OS_NotifyEvent(OS_EVENT_TASK_STARTUP, task_id, NULL);
        if (log != NULL) fprintf(log, "OS_NotifyEvent -> return_code=%ld\n", (long)return_code);
    }

    if (return_code != OS_SUCCESS)
    {
        *entrypt = NULL;
        if (log != NULL) fprintf(log, "Failure path: return_code=%ld, set *entrypt = NULL\n", (long)return_code);
    }

    if (log != NULL)
    {
        fprintf(log, "OS_TaskPrepare EXIT: return_code=%ld\n\n", (long)return_code);
        fclose(log);
    }

    return return_code;
}
