int32 OS_TimeBaseGetInfo(osal_id_t timebase_id, OS_timebase_prop_t *timebase_prop)
{
    OS_common_record_t *           record;
    int32                          return_code;
    osal_objtype_t                 objtype;
    OS_object_token_t              token;
    OS_timebase_internal_record_t *timebase;
    FILE *log = fopen("/tmp/logs/2175.txt", "a");

    if (log)
    {
        fprintf(log, "ENTER OS_TimeBaseGetInfo: timebase_id=%llu, timebase_prop=%p\n",
                (unsigned long long)timebase_id, (void *)timebase_prop);
        fflush(log);
    }

    /* Check parameters */
    OS_CHECK_POINTER(timebase_prop);

    /*
     * Check our context.  Not allowed to use the timer API from a timer callback.
     * Just interested in the object type returned.
     */
    objtype = OS_ObjectIdToType_Impl(OS_TaskGetId_Impl());
    if (log)
    {
        fprintf(log, "objtype=%u\n", (unsigned)objtype);
        fflush(log);
    }

    if (objtype == OS_OBJECT_TYPE_OS_TIMEBASE)
    {
        if (log)
        {
            fprintf(log, "ERROR: called from timer callback, returning OS_ERR_INCORRECT_OBJ_STATE\n");
            fclose(log);
        }
        return OS_ERR_INCORRECT_OBJ_STATE;
    }

    memset(timebase_prop, 0, sizeof(OS_timebase_prop_t));
    if (log)
    {
        fprintf(log, "timebase_prop zeroed\n");
        fflush(log);
    }

    return_code = OS_ObjectIdGetById(OS_LOCK_MODE_GLOBAL, LOCAL_OBJID_TYPE, timebase_id, &token);
    if (log)
    {
        /* Print an address for the token variable so log records that a token was produced */
        fprintf(log, "OS_ObjectIdGetById returned %d, token_addr=%p\n", (int)return_code, (void *)&token);
        fflush(log);
    }

    if (return_code == OS_SUCCESS)
    {
        record   = OS_OBJECT_TABLE_GET(OS_global_timebase_table, token);
        timebase = OS_OBJECT_TABLE_GET(OS_timebase_table, token);

        if (log)
        {
            fprintf(log, "record=%p, timebase=%p\n", (void *)record, (void *)timebase);
            if (record != NULL)
            {
                /* Log name_entry and creator to demonstrate read-only usage of the record pointer */
                const char *namep = record->name_entry ? record->name_entry : "(null)";
                fprintf(log, "record->name_entry=\"%s\", record->creator=%u\n",
                        namep, (unsigned)record->creator);
            }
            else
            {
                fprintf(log, "record is NULL\n");
            }

            if (timebase != NULL)
            {
                fprintf(log, "timebase->nominal_interval_time=%llu, timebase->freerun_time=%llu, timebase->accuracy_usec=%llu\n",
                        (unsigned long long)timebase->nominal_interval_time,
                        (unsigned long long)timebase->freerun_time,
                        (unsigned long long)timebase->accuracy_usec);
            }
            else
            {
                fprintf(log, "timebase is NULL\n");
            }

            fflush(log);
        }

        /* Preserve original behavior: copy from record (read-only) into timebase_prop */
        strncpy(timebase_prop->name, record->name_entry, sizeof(timebase_prop->name) - 1);
        timebase_prop->creator               = record->creator;
        timebase_prop->nominal_interval_time = timebase->nominal_interval_time;
        timebase_prop->freerun_time          = timebase->freerun_time;
        timebase_prop->accuracy              = timebase->accuracy_usec;

        if (log)
        {
            fprintf(log, "populated timebase_prop: name=\"%s\", creator=%u\n",
                    timebase_prop->name, (unsigned)timebase_prop->creator);
            fflush(log);
        }

        return_code = OS_TimeBaseGetInfo_Impl(&token, timebase_prop);
        if (log)
        {
            fprintf(log, "OS_TimeBaseGetInfo_Impl returned %d\n", (int)return_code);
            fflush(log);
        }

        OS_ObjectIdRelease(&token);
        if (log)
        {
            fprintf(log, "OS_ObjectIdRelease called\n");
            fflush(log);
        }
    }

    if (log)
    {
        fprintf(log, "EXIT OS_TimeBaseGetInfo returning %d\n", (int)return_code);
        fclose(log);
    }

    return return_code;
}
