int32 OS_TimeBaseGetInfo(osal_id_t timebase_id, OS_timebase_prop_t *timebase_prop)
{
    OS_common_record_t *           record;
    int32                          return_code;
    osal_objtype_t                 objtype;
    OS_object_token_t              token;
    OS_timebase_internal_record_t *timebase;

    /* Minimal entry log to show function was reached and parameters */
    {
        FILE *log = fopen("/tmp/logs/2175.txt", "a");
        if (log)
        {
            fprintf(log, "ENTER OS_TimeBaseGetInfo: timebase_id=%u, timebase_prop=%p\n",
                    (unsigned)timebase_id, (void *)timebase_prop);
            fclose(log);
        }
    }

    /* Check parameters */
    OS_CHECK_POINTER(timebase_prop);

    /*
     * Check our context.  Not allowed to use the timer API from a timer callback.
     * Just interested in the object type returned.
     */
    objtype = OS_ObjectIdToType_Impl(OS_TaskGetId_Impl());
    if (objtype == OS_OBJECT_TYPE_OS_TIMEBASE)
    {
        FILE *log = fopen("/tmp/logs/2175.txt", "a");
        if (log)
        {
            fprintf(log, "OS_TimeBaseGetInfo: caller context is a timebase callback (objtype=%d). Returning OS_ERR_INCORRECT_OBJ_STATE\n",
                    (int)objtype);
            fclose(log);
        }

        return OS_ERR_INCORRECT_OBJ_STATE;
    }

    memset(timebase_prop, 0, sizeof(OS_timebase_prop_t));

    return_code = OS_ObjectIdGetById(OS_LOCK_MODE_GLOBAL, LOCAL_OBJID_TYPE, timebase_id, &token);
    if (return_code == OS_SUCCESS)
    {
        record   = OS_OBJECT_TABLE_GET(OS_global_timebase_table, token);
        timebase = OS_OBJECT_TABLE_GET(OS_timebase_table, token);

        /* Log the discovered records and the fields read from the non-const timebase pointer */
        {
            FILE *log = fopen("/tmp/logs/2175.txt", "a");
            if (log)
            {
                /* name_entry is expected to be a string in the record; print safely as best-effort */
                fprintf(log, "OS_TimeBaseGetInfo: record=%p, name_entry=\"%s\", creator=%u, timebase_ptr=%p\n",
                        (void *)record,
                        record ? record->name_entry : "(null)",
                        record ? (unsigned)record->creator : 0u,
                        (void *)timebase);

                /* Print numeric time fields we read from the timebase object. Cast to unsigned long long for portability. */
                if (timebase)
                {
                    fprintf(log, "OS_TimeBaseGetInfo: timebase->nominal_interval_time=%llu, timebase->freerun_time=%llu, timebase->accuracy_usec=%llu\n",
                            (unsigned long long)timebase->nominal_interval_time,
                            (unsigned long long)timebase->freerun_time,
                            (unsigned long long)timebase->accuracy_usec);
                }
                else
                {
                    fprintf(log, "OS_TimeBaseGetInfo: timebase pointer is NULL\n");
                }

                fprintf(log, "OS_TimeBaseGetInfo: about to copy fields into timebase_prop (%p)\n", (void *)timebase_prop);
                fclose(log);
            }
        }

        strncpy(timebase_prop->name, record->name_entry, sizeof(timebase_prop->name) - 1);
        timebase_prop->creator               = record->creator;
        timebase_prop->nominal_interval_time = timebase->nominal_interval_time;
        timebase_prop->freerun_time          = timebase->freerun_time;
        timebase_prop->accuracy              = timebase->accuracy_usec;

        return_code = OS_TimeBaseGetInfo_Impl(&token, timebase_prop);

        {
            FILE *log = fopen("/tmp/logs/2175.txt", "a");
            if (log)
            {
                fprintf(log, "OS_TimeBaseGetInfo: returned from OS_TimeBaseGetInfo_Impl, return_code=%d\n", (int)return_code);
                fclose(log);
            }
        }

        OS_ObjectIdRelease(&token);
    }

    {
        FILE *log = fopen("/tmp/logs/2175.txt", "a");
        if (log)
        {
            fprintf(log, "EXIT OS_TimeBaseGetInfo: final return_code=%d\n", (int)return_code);
            fclose(log);
        }
    }

    return return_code;
}
