int32 OS_TimeBaseSet(osal_id_t timer_id, uint32 start_time, uint32 interval_time)
{
    int32                          return_code;
    osal_objtype_t                 objtype;
    OS_object_token_t              token;
    OS_timebase_internal_record_t *timebase;

    /* Open log file for tracing control flow and state. Append mode so repeated runs accumulate logs. */
    {
        FILE *log = fopen("/tmp/logs/1333.txt", "a");
        if (log != NULL)
        {
            fprintf(log, "ENTER OS_TimeBaseSet: timer_id=%llu start_time=%llu interval_time=%llu\n",
                    (unsigned long long)timer_id,
                    (unsigned long long)start_time,
                    (unsigned long long)interval_time);
            fflush(log);
            fclose(log);
        }
    }

    /*
     * Internally the implementation represents the interval as a
     * signed 32-bit integer, but the parameter is unsigned because a negative interval
     * does not make sense.
     *
     * Note that the units are intentionally left unspecified.  The external sync period
     * could be measured in microseconds or hours -- it is whatever the application requires.
     */
    ARGCHECK(start_time < 1000000000, OS_TIMER_ERR_INVALID_ARGS);
    ARGCHECK(interval_time < 1000000000, OS_TIMER_ERR_INVALID_ARGS);

    {
        FILE *log = fopen("/tmp/logs/1333.txt", "a");
        if (log != NULL)
        {
            fprintf(log, "ARGCHECK passed for start_time and interval_time\n");
            fflush(log);
            fclose(log);
        }
    }

    /*
     * Check our context.  Not allowed to use the timer API from a timer callback.
     * Just interested in the object type returned.
     */
    objtype = OS_ObjectIdToType_Impl(OS_TaskGetId_Impl());
    {
        FILE *log = fopen("/tmp/logs/1333.txt", "a");
        if (log != NULL)
        {
            fprintf(log, "OS_TaskGetId_Impl->objtype = %d\n", (int)objtype);
            fflush(log);
            fclose(log);
        }
    }

    if (objtype == OS_OBJECT_TYPE_OS_TIMEBASE)
    {
        FILE *log = fopen("/tmp/logs/1333.txt", "a");
        if (log != NULL)
        {
            fprintf(log, "ERROR: called from timebase context, returning OS_ERR_INCORRECT_OBJ_STATE (%d)\n", (int)OS_ERR_INCORRECT_OBJ_STATE);
            fflush(log);
            fclose(log);
        }

        return OS_ERR_INCORRECT_OBJ_STATE;
    }

    return_code = OS_ObjectIdGetById(OS_LOCK_MODE_GLOBAL, OS_OBJECT_TYPE_OS_TIMEBASE, timer_id, &token);

    {
        FILE *log = fopen("/tmp/logs/1333.txt", "a");
        if (log != NULL)
        {
            /* Log return code and basic token info (address and size). Token contents are implementation-specific. */
            fprintf(log, "OS_ObjectIdGetById returned %d, token_addr=%p, token_size=%zu\n",
                    (int)return_code,
                    (void *)&token,
                    (size_t)sizeof(token));
            fflush(log);
            fclose(log);
        }
    }

    if (return_code == OS_SUCCESS)
    {
        timebase = OS_OBJECT_TABLE_GET(OS_timebase_table, token);

        {
            FILE *log = fopen("/tmp/logs/1333.txt", "a");
            if (log != NULL)
            {
                fprintf(log, "OS_OBJECT_TABLE_GET -> timebase_ptr=%p\n", (void *)timebase);
                fflush(log);
                fclose(log);
            }
        }

        /* Need to take the time base lock to ensure that no ticks are currently being processed */
        {
            FILE *log = fopen("/tmp/logs/1333.txt", "a");
            if (log != NULL)
            {
                fprintf(log, "Calling OS_TimeBaseLock_Impl(&token) token_addr=%p\n", (void *)&token);
                fflush(log);
                fclose(log);
            }
        }
        OS_TimeBaseLock_Impl(&token);
        {
            FILE *log = fopen("/tmp/logs/1333.txt", "a");
            if (log != NULL)
            {
                fprintf(log, "Returned from OS_TimeBaseLock_Impl\n");
                fflush(log);
                fclose(log);
            }
        }

        return_code = OS_TimeBaseSet_Impl(&token, start_time, interval_time);

        {
            FILE *log = fopen("/tmp/logs/1333.txt", "a");
            if (log != NULL)
            {
                fprintf(log, "OS_TimeBaseSet_Impl returned %d\n", (int)return_code);
                fflush(log);
                fclose(log);
            }
        }

        if (return_code == OS_SUCCESS)
        {
            if (timebase != NULL)
            {
                FILE *log = fopen("/tmp/logs/1333.txt", "a");
                if (log != NULL)
                {
                    fprintf(log, "Old nominal_start_time=%llu nominal_interval_time=%llu\n",
                            (unsigned long long)timebase->nominal_start_time,
                            (unsigned long long)timebase->nominal_interval_time);
                    fflush(log);
                    fclose(log);
                }

                timebase->nominal_start_time    = start_time;
                timebase->nominal_interval_time = interval_time;

                log = fopen("/tmp/logs/1333.txt", "a");
                if (log != NULL)
                {
                    fprintf(log, "Updated nominal_start_time=%llu nominal_interval_time=%llu\n",
                            (unsigned long long)timebase->nominal_start_time,
                            (unsigned long long)timebase->nominal_interval_time);
                    fflush(log);
                    fclose(log);
                }
            }
            else
            {
                FILE *log = fopen("/tmp/logs/1333.txt", "a");
                if (log != NULL)
                {
                    fprintf(log, "WARNING: timebase pointer is NULL - cannot update nominal values\n");
                    fflush(log);
                    fclose(log);
                }
            }
        }

        OS_TimeBaseUnlock_Impl(&token);

        {
            FILE *log = fopen("/tmp/logs/1333.txt", "a");
            if (log != NULL)
            {
                fprintf(log, "Called OS_TimeBaseUnlock_Impl(&token) and OS_ObjectIdRelease(&token) next\n");
                fflush(log);
                fclose(log);
            }
        }

        OS_ObjectIdRelease(&token);

        {
            FILE *log = fopen("/tmp/logs/1333.txt", "a");
            if (log != NULL)
            {
                fprintf(log, "OS_ObjectIdRelease completed for token_addr=%p\n", (void *)&token);
                fflush(log);
                fclose(log);
            }
        }
    }

    {
        FILE *log = fopen("/tmp/logs/1333.txt", "a");
        if (log != NULL)
        {
            fprintf(log, "EXIT OS_TimeBaseSet: return_code=%d\n", (int)return_code);
            fflush(log);
            fclose(log);
        }
    }

    return return_code;
}
