int32 OS_TimerDelete(osal_id_t timer_id)
{
    int32                          return_code;
    osal_objtype_t                 objtype;
    osal_id_t                      dedicated_timebase_id;
    OS_object_token_t              timecb_token;
    OS_object_token_t              timebase_token;
    OS_object_token_t              listcb_token;
    OS_timebase_internal_record_t *timebase;
    OS_timecb_internal_record_t *  timecb;
    OS_timecb_internal_record_t *  list_timecb;

    /* Open log file for probes */
    FILE *log = fopen("/tmp/logs/2220.txt", "a");
    if (log)
    {
        fprintf(log, "OS_TimerDelete ENTRY: timer_id=%u\n", (unsigned)timer_id);
        fflush(log);
    }

    dedicated_timebase_id = OS_OBJECT_ID_UNDEFINED;
    memset(&timebase_token, 0, sizeof(timebase_token));

    /*
     * Check our context.  Not allowed to use the timer API from a timer callback.
     * Just interested in the object type returned.
     */
    objtype = OS_ObjectIdToType_Impl(OS_TaskGetId_Impl());
    if (log)
    {
        fprintf(log, "OS_TimerDelete: caller objtype=%d\n", (int)objtype);
        fflush(log);
    }

    if (objtype == OS_OBJECT_TYPE_OS_TIMEBASE)
    {
        if (log)
        {
            fprintf(log, "OS_TimerDelete: rejected - called from timebase callback context\n");
            fflush(log);
            fclose(log);
        }
        return OS_ERR_INCORRECT_OBJ_STATE;
    }

    return_code = OS_ObjectIdGetById(OS_LOCK_MODE_EXCLUSIVE, OS_OBJECT_TYPE_OS_TIMECB, timer_id, &timecb_token);
    if (log)
    {
        fprintf(log, "OS_TimerDelete: OS_ObjectIdGetById returned %d\n", (int)return_code);
        fflush(log);
    }

    if (return_code == OS_SUCCESS)
    {
        /* fetch table entries */
        timecb   = OS_OBJECT_TABLE_GET(OS_timecb_table, timecb_token);
        timebase = OS_OBJECT_TABLE_GET(OS_timebase_table, timecb->timebase_token);

        if (log)
        {
            fprintf(log, "OS_TimerDelete: timecb_id=%u timebase_id=%u flags=0x%08x\n",
                    (unsigned)OS_ObjectIdFromToken(&timecb_token),
                    (unsigned)OS_ObjectIdFromToken(&timecb->timebase_token),
                    (unsigned)timecb->flags);
            fflush(log);
        }

        OS_ObjectIdTransferToken(&timecb->timebase_token, &timebase_token);

        if (log)
        {
            fprintf(log, "OS_TimerDelete: transferred timebase token -> timebase_token_id=%u\n",
                    (unsigned)OS_ObjectIdFromToken(&timebase_token));
            fflush(log);
        }

        OS_TimeBaseLock_Impl(&timecb->timebase_token);
        if (log)
        {
            fprintf(log, "OS_TimerDelete: Time base locked (timebase_token=%u)\n",
                    (unsigned)OS_ObjectIdFromToken(&timecb->timebase_token));
            fflush(log);
        }

        /*
         * If the timer uses a dedicated time base, then also delete that.
         */
        if ((timecb->flags & TIMECB_FLAG_DEDICATED_TIMEBASE) != 0)
        {
            dedicated_timebase_id = OS_ObjectIdFromToken(&timecb->timebase_token);
            if (log)
            {
                fprintf(log, "OS_TimerDelete: has dedicated timebase id=%u\n", (unsigned)dedicated_timebase_id);
                fflush(log);
            }
        }

        /*
         * Now we need to remove it from the time base callback ring
         */
        if (OS_ObjectIdEqual(timebase->first_cb, OS_ObjectIdFromToken(&timecb_token)))
        {
            if (OS_ObjectIdEqual(OS_ObjectIdFromToken(&timecb_token), timecb->next_cb))
            {
                timebase->first_cb = OS_OBJECT_ID_UNDEFINED;
                if (log)
                {
                    fprintf(log, "OS_TimerDelete: was only cb in ring, cleared first_cb\n");
                    fflush(log);
                }
            }
            else
            {
                timebase->first_cb = timecb->next_cb;
                if (log)
                {
                    fprintf(log, "OS_TimerDelete: first_cb moved to next_cb=%u\n", (unsigned)timecb->next_cb);
                    fflush(log);
                }
            }
        }

        if (OS_ObjectIdGetById(OS_LOCK_MODE_NONE, OS_OBJECT_TYPE_OS_TIMECB, timecb->prev_cb, &listcb_token) ==
            OS_SUCCESS)
        {
            list_timecb          = OS_OBJECT_TABLE_GET(OS_timecb_table, listcb_token);
            list_timecb->next_cb = timecb->next_cb;
            if (log)
            {
                fprintf(log, "OS_TimerDelete: patched prev_cb (id=%u) next_cb -> %u\n",
                        (unsigned)OS_ObjectIdFromToken(&listcb_token), (unsigned)timecb->next_cb);
                fflush(log);
            }
        }
        if (OS_ObjectIdGetById(OS_LOCK_MODE_NONE, OS_OBJECT_TYPE_OS_TIMECB, timecb->next_cb, &listcb_token) ==
            OS_SUCCESS)
        {
            list_timecb          = OS_OBJECT_TABLE_GET(OS_timecb_table, listcb_token);
            list_timecb->prev_cb = timecb->prev_cb;
            if (log)
            {
                fprintf(log, "OS_TimerDelete: patched next_cb (id=%u) prev_cb -> %u\n",
                        (unsigned)OS_ObjectIdFromToken(&listcb_token), (unsigned)timecb->prev_cb);
                fflush(log);
            }
        }

        /*
         * Re-initialize the deleted element to be a self loop so it won't point at others.
         */
        timecb->next_cb = OS_ObjectIdFromToken(&timecb_token);
        timecb->prev_cb = OS_ObjectIdFromToken(&timecb_token);
        if (log)
        {
            fprintf(log, "OS_TimerDelete: isolated timecb (id=%u) next/prev -> self\n", (unsigned)OS_ObjectIdFromToken(&timecb_token));
            fflush(log);
        }

        OS_TimeBaseUnlock_Impl(&timecb->timebase_token);
        if (log)
        {
            fprintf(log, "OS_TimerDelete: Time base unlocked (timebase_token=%u)\n",
                    (unsigned)OS_ObjectIdFromToken(&timecb->timebase_token));
            fflush(log);
        }

        /* Complete the operation via the common routine */
        return_code = OS_ObjectIdFinalizeDelete(return_code, &timecb_token);
        if (log)
        {
            fprintf(log, "OS_TimerDelete: OS_ObjectIdFinalizeDelete returned %d\n", (int)return_code);
            fflush(log);
        }
    }

    /*
     * Remove the reference count against the timebase
     */
    OS_ObjectIdRelease(&timebase_token);
    if (log)
    {
        fprintf(log, "OS_TimerDelete: OS_ObjectIdRelease called on timebase_token (id=%u)\n",
                (unsigned)OS_ObjectIdFromToken(&timebase_token));
        fflush(log);
    }

    /*
     * If the timer uses a dedicated time base, then also delete it.
     */
    if (return_code == OS_SUCCESS && OS_ObjectIdDefined(dedicated_timebase_id))
    {
        if (log)
        {
            fprintf(log, "OS_TimerDelete: about to delete dedicated timebase id=%u\n", (unsigned)dedicated_timebase_id);
            fflush(log);
        }

        /* Important probe: this call may indirectly call into other event handlers. */
        OS_TimeBaseDelete(dedicated_timebase_id);

        if (log)
        {
            fprintf(log, "OS_TimerDelete: OS_TimeBaseDelete returned (dedicated id=%u)\n", (unsigned)dedicated_timebase_id);
            fflush(log);
        }
    }

    if (log)
    {
        fprintf(log, "OS_TimerDelete EXIT: return_code=%d\n", (int)return_code);
        fflush(log);
        fclose(log);
    }

    return return_code;
}
