int32 OS_TimerDelete(osal_id_t timer_id)
{
    int32                          return_code;
    osal_objtype_t                 objtype;
    osal_id_t                      dedicated_timebase_id;
    OS_object_token_t              timecb_token;
    OS_object_token_t              timebase_token;
    OS_object_token_t              listcb_token;
    OS_timebase_internal_record_t *timebase;
    OS_timecb_internal_record_t *  timecb;
    OS_timecb_internal_record_t *  list_timecb;

    /* Logging: open file once per call (append). Best-effort; if fopen fails, continue silently. */
    FILE *___log_fp = fopen("/tmp/logs/2220.txt", "a");
    if (___log_fp)
    {
        fprintf(___log_fp, "ENTER OS_TimerDelete: timer_id=%ld\n", (long)timer_id);
        fflush(___log_fp);
    }

    dedicated_timebase_id = OS_OBJECT_ID_UNDEFINED;
    memset(&timebase_token, 0, sizeof(timebase_token));

    /*
     * Check our context.  Not allowed to use the timer API from a timer callback.
     * Just interested in the object type returned.
     */
    objtype = OS_ObjectIdToType_Impl(OS_TaskGetId_Impl());
    if (___log_fp)
    {
        fprintf(___log_fp, "  Context object type = %d\n", (int)objtype);
        fflush(___log_fp);
    }

    if (objtype == OS_OBJECT_TYPE_OS_TIMEBASE)
    {
        if (___log_fp)
        {
            fprintf(___log_fp, "  Early return: called from timebase context -> OS_ERR_INCORRECT_OBJ_STATE\n");
            fclose(___log_fp);
        }
        return OS_ERR_INCORRECT_OBJ_STATE;
    }

    return_code = OS_ObjectIdGetById(OS_LOCK_MODE_EXCLUSIVE, OS_OBJECT_TYPE_OS_TIMECB, timer_id, &timecb_token);
    if (___log_fp)
    {
        fprintf(___log_fp, "  OS_ObjectIdGetById returned %ld\n", (long)return_code);
        fflush(___log_fp);
    }

    if (return_code == OS_SUCCESS)
    {
        timecb   = OS_OBJECT_TABLE_GET(OS_timecb_table, timecb_token);
        timebase = OS_OBJECT_TABLE_GET(OS_timebase_table, timecb->timebase_token);

        if (___log_fp)
        {
            fprintf(___log_fp, "  timecb token -> id=%ld, timebase token -> id=%ld\n",
                    (long)OS_ObjectIdFromToken(&timecb_token), (long)OS_ObjectIdFromToken(&timecb->timebase_token));
            fflush(___log_fp);
        }

        OS_ObjectIdTransferToken(&timecb->timebase_token, &timebase_token);

        if (___log_fp)
        {
            fprintf(___log_fp, "  Transferred timebase token; locking timebase (id=%ld)\n",
                    (long)OS_ObjectIdFromToken(&timebase_token));
            fflush(___log_fp);
        }

        OS_TimeBaseLock_Impl(&timecb->timebase_token);

        /*
         * If the timer uses a dedicated time base, then also delete that.
         */
        if ((timecb->flags & TIMECB_FLAG_DEDICATED_TIMEBASE) != 0)
        {
            dedicated_timebase_id = OS_ObjectIdFromToken(&timecb->timebase_token);
            if (___log_fp)
            {
                fprintf(___log_fp, "  Timer uses dedicated timebase id=%ld\n", (long)dedicated_timebase_id);
                fflush(___log_fp);
            }
        }

        /*
         * Now we need to remove it from the time base callback ring
         */
        if (OS_ObjectIdEqual(timebase->first_cb, OS_ObjectIdFromToken(&timecb_token)))
        {
            if (OS_ObjectIdEqual(OS_ObjectIdFromToken(&timecb_token), timecb->next_cb))
            {
                timebase->first_cb = OS_OBJECT_ID_UNDEFINED;
                if (___log_fp)
                {
                    fprintf(___log_fp, "  Removed sole callback from timebase; first_cb now UNDEFINED\n");
                    fflush(___log_fp);
                }
            }
            else
            {
                timebase->first_cb = timecb->next_cb;
                if (___log_fp)
                {
                    fprintf(___log_fp, "  Removed first callback; new first_cb=%ld\n", (long)timebase->first_cb);
                    fflush(___log_fp);
                }
            }
        }

        if (OS_ObjectIdGetById(OS_LOCK_MODE_NONE, OS_OBJECT_TYPE_OS_TIMECB, timecb->prev_cb, &listcb_token) ==
            OS_SUCCESS)
        {
            list_timecb          = OS_OBJECT_TABLE_GET(OS_timecb_table, listcb_token);
            list_timecb->next_cb = timecb->next_cb;
            if (___log_fp)
            {
                fprintf(___log_fp, "  Patched prev_cb (%ld) next_cb -> %ld\n",
                        (long)timecb->prev_cb, (long)timecb->next_cb);
                fflush(___log_fp);
            }
        }
        if (OS_ObjectIdGetById(OS_LOCK_MODE_NONE, OS_OBJECT_TYPE_OS_TIMECB, timecb->next_cb, &listcb_token) ==
            OS_SUCCESS)
        {
            list_timecb          = OS_OBJECT_TABLE_GET(OS_timecb_table, listcb_token);
            list_timecb->prev_cb = timecb->prev_cb;
            if (___log_fp)
            {
                fprintf(___log_fp, "  Patched next_cb (%ld) prev_cb -> %ld\n",
                        (long)timecb->next_cb, (long)timecb->prev_cb);
                fflush(___log_fp);
            }
        }

        timecb->next_cb = OS_ObjectIdFromToken(&timecb_token);
        timecb->prev_cb = OS_ObjectIdFromToken(&timecb_token);

        if (___log_fp)
        {
            fprintf(___log_fp, "  Detached timecb (%ld) from list; set next/prev to self\n", (long)OS_ObjectIdFromToken(&timecb_token));
            fflush(___log_fp);
        }

        OS_TimeBaseUnlock_Impl(&timecb->timebase_token);

        /* Complete the operation via the common routine */
        if (___log_fp)
        {
            fprintf(___log_fp, "  Calling OS_ObjectIdFinalizeDelete for id=%ld\n", (long)OS_ObjectIdFromToken(&timecb_token));
            fflush(___log_fp);
        }

        return_code = OS_ObjectIdFinalizeDelete(return_code, &timecb_token);

        if (___log_fp)
        {
            fprintf(___log_fp, "  Returned from OS_ObjectIdFinalizeDelete with return_code=%ld\n", (long)return_code);
            fflush(___log_fp);
        }
    }

    /*
     * Remove the reference count against the timebase
     */
    if (___log_fp)
    {
        fprintf(___log_fp, "  Releasing timebase token id=%ld\n", (long)OS_ObjectIdFromToken(&timebase_token));
        fflush(___log_fp);
    }
    OS_ObjectIdRelease(&timebase_token);

    /*
     * If the timer uses a dedicated time base, then also delete it.
     */
    if (return_code == OS_SUCCESS && OS_ObjectIdDefined(dedicated_timebase_id))
    {
        if (___log_fp)
        {
            fprintf(___log_fp, "  Deleting dedicated timebase id=%ld via OS_TimeBaseDelete\n", (long)dedicated_timebase_id);
            fflush(___log_fp);
        }

        /* This call may trigger user event handlers which could re-enter OS_TimerDelete. */
        OS_TimeBaseDelete(dedicated_timebase_id);

        if (___log_fp)
        {
            fprintf(___log_fp, "  Returned from OS_TimeBaseDelete(id=%ld)\n", (long)dedicated_timebase_id);
            fflush(___log_fp);
        }
    }

    if (___log_fp)
    {
        fprintf(___log_fp, "EXIT OS_TimerDelete: returning %ld for timer_id=%ld\n", (long)return_code, (long)timer_id);
        fclose(___log_fp);
    }

    return return_code;
}
