int32 OS_TimerGetIdByName(osal_id_t *timer_id, const char *timer_name)
{
    int32          return_code;
    osal_objtype_t objtype;
    osal_id_t      task_id;
    FILE          *log = fopen("/tmp/logs/3320.txt", "a");

    /* Minimal entry log so a test run always produces evidence this function was reached */
    if (log)
    {
        fprintf(log, "ENTER: OS_TimerGetIdByName(timer_id=%p, timer_name=%s)\n",
                (void *)timer_id,
                (timer_name != NULL) ? timer_name : "(null)");
    }

    /* Check parameters (preserve original behavior) */
    OS_CHECK_POINTER(timer_id);
    OS_CHECK_POINTER(timer_name);

    /*
     * Check our context.  Not allowed to use the timer API from a timer callback.
     * Capture the task id and object type for logging to help reproduce indirect recursion paths.
     */
    task_id = OS_TaskGetId_Impl();
    if (log)
    {
        /* Print numeric representation of the task id; cast to long for safer printing on various platforms */
        fprintf(log, "OS_TaskGetId_Impl() returned id=%ld\n", (long)task_id);
    }

    objtype = OS_ObjectIdToType_Impl(task_id);
    if (log)
    {
        fprintf(log, "OS_ObjectIdToType_Impl(task_id) returned objtype=%d\n", (int)objtype);
    }

    if (objtype == OS_OBJECT_TYPE_OS_TIMEBASE)
    {
        if (log)
        {
            fprintf(log, "Context check failed: objtype == OS_OBJECT_TYPE_OS_TIMEBASE; returning OS_ERR_INCORRECT_OBJ_STATE\n");
            fclose(log);
        }
        return OS_ERR_INCORRECT_OBJ_STATE;
    }

    if (log)
    {
        fprintf(log, "Calling OS_ObjectIdFindByName(OS_OBJECT_TYPE_OS_TIMECB, \"%s\", %p)\n",
                timer_name,
                (void *)timer_id);
    }

    return_code = OS_ObjectIdFindByName(OS_OBJECT_TYPE_OS_TIMECB, timer_name, timer_id);

    if (log)
    {
        fprintf(log, "OS_ObjectIdFindByName returned %d; timer_id after call=%p\n",
                (int)return_code,
                (void *)timer_id);
        fclose(log);
    }

    return return_code;
}
