int32 OS_TimerGetInfo(osal_id_t timer_id, OS_timer_prop_t *timer_prop)
{
    OS_common_record_t *           record;
    int32                          return_code;
    osal_objtype_t                 objtype;
    OS_object_token_t              token;
    OS_timecb_internal_record_t *  timecb;
    OS_timebase_internal_record_t *timebase;
    FILE *logf = fopen("/tmp/logs/2595.txt", "a");

    /* Minimal entry log so callers know the function was reached. */
    if (logf)
    {
        fprintf(logf, "ENTER: OS_TimerGetInfo(timer_id=%ld, timer_prop=%p)\n", (long)timer_id, (void *)timer_prop);
        fflush(logf);
    }

    /* Check parameters (preserve original behavior/order) */
    OS_CHECK_POINTER(timer_prop);

    /*
     * Check our context.  Not allowed to use the timer API from a timer callback.
     * Just interested in the object type returned.
     */
    objtype = OS_ObjectIdToType_Impl(OS_TaskGetId_Impl());
    if (objtype == OS_OBJECT_TYPE_OS_TIMEBASE)
    {
        if (logf)
        {
            fprintf(logf, "EARLY RETURN: incorrect context, objtype == OS_OBJECT_TYPE_OS_TIMEBASE (%d)\n", (int)objtype);
            fclose(logf);
        }
        return OS_ERR_INCORRECT_OBJ_STATE;
    }

    memset(timer_prop, 0, sizeof(OS_timer_prop_t));

    return_code = OS_ObjectIdGetById(OS_LOCK_MODE_GLOBAL, OS_OBJECT_TYPE_OS_TIMECB, timer_id, &token);
    if (logf)
    {
        fprintf(logf, "OS_ObjectIdGetById returned %d (looking up timer_id=%ld)\n", (int)return_code, (long)timer_id);
        fflush(logf);
    }

    if (return_code == OS_SUCCESS)
    {
        record   = OS_OBJECT_TABLE_GET(OS_global_timecb_table, token);
        timecb   = OS_OBJECT_TABLE_GET(OS_timecb_table, token);
        timebase = OS_OBJECT_TABLE_GET(OS_timebase_table, timecb->timebase_token);

        /* Log key pieces of state to help reproduce the read-only usage of 'record' */
        if (logf)
        {
            /* Safely print strings and pointers; avoid assuming token is printable as an integer */
            fprintf(logf, "token@%p (size=%zu), record=%p, timecb=%p, timebase=%p\n",
                    (void *)&token, sizeof(token), (void *)record, (void *)timecb, (void *)timebase);

            if (record)
            {
                fprintf(logf, "record->name_entry=\"%s\", record->creator=%u\n",
                        record->name_entry ? record->name_entry : "(null)", (unsigned)record->creator);
            }
            else
            {
                fprintf(logf, "record is NULL\n");
            }

            if (timecb)
            {
                fprintf(logf, "timecb->interval_time=%lu\n", (unsigned long)timecb->interval_time);
            }
            else
            {
                fprintf(logf, "timecb is NULL\n");
            }

            if (timebase)
            {
                fprintf(logf, "timebase->accuracy_usec=%lu\n", (unsigned long)timebase->accuracy_usec);
            }
            else
            {
                fprintf(logf, "timebase is NULL\n");
            }

            fflush(logf);
        }

        /* Preserve original behavior: copy values into the output structure */
        strncpy(timer_prop->name, record->name_entry, sizeof(timer_prop->name) - 1);
        timer_prop->creator       = record->creator;
        timer_prop->interval_time = (uint32)timecb->interval_time;
        timer_prop->accuracy      = timebase->accuracy_usec;

        OS_ObjectIdRelease(&token);

        if (logf)
        {
            fprintf(logf, "Released token for timer_id=%ld\n", (long)timer_id);
            fflush(logf);
        }
    }

    if (logf)
    {
        fprintf(logf, "EXIT: OS_TimerGetInfo returning %d for timer_id=%ld\n", (int)return_code, (long)timer_id);
        fclose(logf);
    }

    return return_code;
}
