int32 OS_TimerGetInfo(osal_id_t timer_id, OS_timer_prop_t *timer_prop)
{
    OS_common_record_t *           record;
    int32                          return_code;
    osal_objtype_t                 objtype;
    OS_object_token_t              token;
    OS_timecb_internal_record_t *  timecb;
    OS_timebase_internal_record_t *timebase;

    /* Open log file for probes (append so multiple runs accumulate evidence) */
    FILE *log = fopen("/tmp/logs/2595.txt", "a");
    if (log)
    {
        fprintf(log, "ENTER: OS_TimerGetInfo(timer_id=%u, timer_prop=%p)\n", (unsigned)timer_id, (void *)timer_prop);
    }

    /* Check parameters */
    OS_CHECK_POINTER(timer_prop);

    /*
     * Check our context.  Not allowed to use the timer API from a timer callback.
     * Just interested in the object type returned.
     */
    objtype = OS_ObjectIdToType_Impl(OS_TaskGetId_Impl());
    if (log)
    {
        fprintf(log, "context: current objtype=%d\n", (int)objtype);
    }

    if (objtype == OS_OBJECT_TYPE_OS_TIMEBASE)
    {
        if (log)
        {
            fprintf(log, "EXIT: OS_TimerGetInfo -> OS_ERR_INCORRECT_OBJ_STATE (called from timebase)\n");
            fclose(log);
        }
        return OS_ERR_INCORRECT_OBJ_STATE;
    }

    memset(timer_prop, 0, sizeof(OS_timer_prop_t));
    if (log)
    {
        fprintf(log, "timer_prop zeroed\n");
    }

    return_code = OS_ObjectIdGetById(OS_LOCK_MODE_GLOBAL, OS_OBJECT_TYPE_OS_TIMECB, timer_id, &token);
    if (log)
    {
        fprintf(log, "OS_ObjectIdGetById returned %d, token=%u\n", (int)return_code, (unsigned)token);
    }

    if (return_code == OS_SUCCESS)
    {
        /* Retrieve internal records from tables */
        record   = OS_OBJECT_TABLE_GET(OS_global_timecb_table, token);
        timecb   = OS_OBJECT_TABLE_GET(OS_timecb_table, token);
        timebase = OS_OBJECT_TABLE_GET(OS_timebase_table, timecb->timebase_token);

        /* Log the pointers and key fields to capture the violation scenario evidence. */
        if (log)
        {
            fprintf(log, "record ptr=%p, timecb ptr=%p, timebase ptr=%p\n", (void *)record, (void *)timecb, (void *)timebase);

            /* record->name_entry is read-only in this routine; logging its value documents that it was not modified */
            if (record && record->name_entry)
            {
                /* Ensure we don't write an unterminated or overly long string into the log */
                fprintf(log, "record->name_entry='%s'\n", record->name_entry);
            }
            else
            {
                fprintf(log, "record->name_entry=(null)\n");
            }

            fprintf(log, "record->creator=%u\n", (unsigned)(record ? record->creator : 0u));

            /* Log timecb/timebase numeric fields used below */
            fprintf(log, "timecb->interval_time=%u, timebase->accuracy_usec=%u\n",
                    (unsigned)timecb->interval_time, (unsigned)timebase->accuracy_usec);

            /* Explicit note capturing the MISRA-8.13 relevant observation */
            fprintf(log, "NOTE: local variable 'record' is declared as OS_common_record_t * but only read from in this function; consider 'const OS_common_record_t *'\n");
        }

        /* Preserve original behavior: copy data into the public timer_prop structure */
        strncpy(timer_prop->name, record->name_entry, sizeof(timer_prop->name) - 1);
        timer_prop->creator       = record->creator;
        timer_prop->interval_time = (uint32)timecb->interval_time;
        timer_prop->accuracy      = timebase->accuracy_usec;

        OS_ObjectIdRelease(&token);

        if (log)
        {
            fprintf(log, "timer_prop populated: name='%s', creator=%u, interval_time=%u, accuracy=%u\n",
                    timer_prop->name, (unsigned)timer_prop->creator, (unsigned)timer_prop->interval_time, (unsigned)timer_prop->accuracy);
        }
    }

    if (log)
    {
        fprintf(log, "EXIT: OS_TimerGetInfo returning %d\n", (int)return_code);
        fclose(log);
    }

    return return_code;
}
