int32 OS_TimerGetInfo(osal_id_t timer_id, OS_timer_prop_t *timer_prop)
{
    OS_common_record_t *           record;
    int32                          return_code;
    osal_objtype_t                 objtype;
    OS_object_token_t              token;
    OS_timecb_internal_record_t *  timecb;
    OS_timebase_internal_record_t *timebase;

    /* Probe log: open file and record entry */
    {
        FILE *logf = fopen("/tmp/logs/2595.txt", "a");
        if (logf)
        {
            /* Log function entry and raw parameter values */
            fprintf(logf, "OS_TimerGetInfo ENTRY: timer_id=%ld, timer_prop=%p\n",
                    (long)timer_id, (void *)timer_prop);
            fclose(logf);
        }
    }

    /* Check parameters */
    OS_CHECK_POINTER(timer_prop);

    /*
     * Check our context.  Not allowed to use the timer API from a timer callback.
     * Just interested in the object type returned.
     */
    objtype = OS_ObjectIdToType_Impl(OS_TaskGetId_Impl());

    /* Log the context object type */
    {
        FILE *logf = fopen("/tmp/logs/2595.txt", "a");
        if (logf)
        {
            fprintf(logf, "OS_TimerGetInfo: context objtype=%d\n", (int)objtype);
            fclose(logf);
        }
    }

    if (objtype == OS_OBJECT_TYPE_OS_TIMEBASE)
    {
        /* Log the incorrect object state branch */
        FILE *logf = fopen("/tmp/logs/2595.txt", "a");
        if (logf)
        {
            fprintf(logf, "OS_TimerGetInfo EXIT: incorrect object state (called from timebase)\n");
            fclose(logf);
        }
        return OS_ERR_INCORRECT_OBJ_STATE;
    }

    memset(timer_prop, 0, sizeof(OS_timer_prop_t));

    return_code = OS_ObjectIdGetById(OS_LOCK_MODE_GLOBAL, OS_OBJECT_TYPE_OS_TIMECB, timer_id, &token);

    /* Log the result of the object id lookup */
    {
        FILE *logf = fopen("/tmp/logs/2595.txt", "a");
        if (logf)
        {
            fprintf(logf, "OS_TimerGetInfo: OS_ObjectIdGetById return_code=%d, token_addr=%p\n",
                    (int)return_code, (void *)&token);
            fclose(logf);
        }
    }

    if (return_code == OS_SUCCESS)
    {
        record   = OS_OBJECT_TABLE_GET(OS_global_timecb_table, token);
        timecb   = OS_OBJECT_TABLE_GET(OS_timecb_table, token);
        timebase = OS_OBJECT_TABLE_GET(OS_timebase_table, timecb->timebase_token);

        /* Log the pointers and key fields observed to demonstrate the read-only use */
        {
            FILE *logf = fopen("/tmp/logs/2595.txt", "a");
            if (logf)
            {
                /* record may contain a name; guard printing with safe formats */
                const char *rname = "(null)";
                if (record && record->name_entry)
                {
                    rname = record->name_entry;
                }

                fprintf(logf, "OS_TimerGetInfo: record=%p, record->name_entry=\"%s\"\n",
                        (void *)record, rname);

                /* Print creator and interval_time if available */
                if (record)
                {
                    fprintf(logf, "OS_TimerGetInfo: record->creator=%llu\n",
                            (unsigned long long)record->creator);
                }

                if (timecb)
                {
                    fprintf(logf, "OS_TimerGetInfo: timecb=%p, timecb->interval_time=%llu, timecb->timebase_token=%p\n",
                            (void *)timecb,
                            (unsigned long long)timecb->interval_time,
                            (void *)(uintptr_t)timecb->timebase_token);
                }

                if (timebase)
                {
                    /* Capture the read-only field used below to demonstrate the violation scenario */
                    fprintf(logf, "OS_TimerGetInfo: timebase=%p, timebase->accuracy_usec=%llu\n",
                            (void *)timebase,
                            (unsigned long long)timebase->accuracy_usec);
                }

                fclose(logf);
            }
        }

        strncpy(timer_prop->name, record->name_entry, sizeof(timer_prop->name) - 1);
        timer_prop->creator       = record->creator;
        timer_prop->interval_time = (uint32)timecb->interval_time;
        timer_prop->accuracy      = timebase->accuracy_usec;

        OS_ObjectIdRelease(&token);
    }

    /* Final exit log with return code */
    {
        FILE *logf = fopen("/tmp/logs/2595.txt", "a");
        if (logf)
        {
            fprintf(logf, "OS_TimerGetInfo EXIT: return_code=%d\n", (int)return_code);
            fclose(logf);
        }
    }

    return return_code;
}
