int32 OS_TimerGetInfo(osal_id_t timer_id, OS_timer_prop_t *timer_prop)
{
    OS_common_record_t *           record;
    int32                          return_code;
    osal_objtype_t                 objtype;
    OS_object_token_t              token;
    OS_timecb_internal_record_t *  timecb;
    OS_timebase_internal_record_t *timebase;

    /* Open a log file for tracing. Use append so multiple calls accumulate evidence. */
    FILE *log = fopen("/tmp/logs/2595.txt", "a");
    if (log)
    {
        fprintf(log, "OS_TimerGetInfo ENTER: timer_id=%lu\n", (unsigned long)timer_id);
        fclose(log);
    }

    /* Check parameters */
    OS_CHECK_POINTER(timer_prop);

    /*
     * Check our context.  Not allowed to use the timer API from a timer callback.
     * Just interested in the object type returned.
     */
    objtype = OS_ObjectIdToType_Impl(OS_TaskGetId_Impl());
    if (objtype == OS_OBJECT_TYPE_OS_TIMEBASE)
    {
        log = fopen("/tmp/logs/2595.txt", "a");
        if (log)
        {
            fprintf(log, "OS_TimerGetInfo EARLY_EXIT: caller is a timebase (incorrect obj state)\n");
            fclose(log);
        }
        return OS_ERR_INCORRECT_OBJ_STATE;
    }

    memset(timer_prop, 0, sizeof(OS_timer_prop_t));

    return_code = OS_ObjectIdGetById(OS_LOCK_MODE_GLOBAL, OS_OBJECT_TYPE_OS_TIMECB, timer_id, &token);

    log = fopen("/tmp/logs/2595.txt", "a");
    if (log)
    {
        fprintf(log, "OS_ObjectIdGetById returned %d, token=%lu\n", (int)return_code, (unsigned long)token);
        fclose(log);
    }

    if (return_code == OS_SUCCESS)
    {
        record   = OS_OBJECT_TABLE_GET(OS_global_timecb_table, token);
        timecb   = OS_OBJECT_TABLE_GET(OS_timecb_table, token);
        /* Log the timebase_token from the timecb (read-only access in original code) */
        log = fopen("/tmp/logs/2595.txt", "a");
        if (log)
        {
            fprintf(log, "record=%p, timecb=%p\n", (void *)record, (void *)timecb);
            if (record)
            {
                /* name_entry is expected to be a NUL-terminated string in record */
                fprintf(log, "record->name_entry=\"%s\", record->creator=%lu\n",
                        record->name_entry, (unsigned long)record->creator);
            }
            if (timecb)
            {
                fprintf(log, "timecb->timebase_token=%lu, timecb->interval_time=%lu\n",
                        (unsigned long)timecb->timebase_token,
                        (unsigned long)timecb->interval_time);
            }
            fclose(log);
        }

        timebase = OS_OBJECT_TABLE_GET(OS_timebase_table, timecb->timebase_token);

        log = fopen("/tmp/logs/2595.txt", "a");
        if (log)
        {
            fprintf(log, "timebase=%p, timebase->accuracy_usec=%lu\n", (void *)timebase,
                    (unsigned long)(timebase ? timebase->accuracy_usec : 0));
            fclose(log);
        }

        /* Preserve original behavior: copy properties into timer_prop */
        strncpy(timer_prop->name, record->name_entry, sizeof(timer_prop->name) - 1);
        timer_prop->creator       = record->creator;
        timer_prop->interval_time = (uint32)timecb->interval_time;
        timer_prop->accuracy      = timebase->accuracy_usec;

        OS_ObjectIdRelease(&token);
    }

    log = fopen("/tmp/logs/2595.txt", "a");
    if (log)
    {
        fprintf(log, "OS_TimerGetInfo EXIT: return_code=%d\n", (int)return_code);
        fclose(log);
    }

    return return_code;
}
