int32 OS_TranslatePath(const char *VirtualPath, char *LocalPath)
{
    OS_object_token_t             token;
    int32                         return_code;
    const char *                  name_ptr;
    OS_filesys_internal_record_t *filesys;
    size_t                        SysMountPointLen;
    size_t                        VirtPathLen;
    size_t                        VirtPathBegin;

    /* Open log for probing control flow/state. Best-effort; do not alter behavior on failure. */
    FILE *log = fopen("/tmp/logs/1718.txt", "a");
    if (log)
    {
        fprintf(log, "OS_TranslatePath ENTER: VirtualPath=%p LocalPath=%p\n", (const void *)VirtualPath, (const void *)LocalPath);
        fflush(log);
    }

    /* Check to see if the path pointers are NULL */
    /* Check parameters */
    OS_CHECK_POINTER(VirtualPath);
    OS_CHECK_POINTER(LocalPath);

    /* Check length */
    VirtPathLen = OS_strnlen(VirtualPath, OS_MAX_PATH_LEN);
    if (log)
    {
        fprintf(log, "Computed VirtPathLen=%zu\n", VirtPathLen);
        fflush(log);
    }
    if (VirtPathLen >= OS_MAX_PATH_LEN)
    {
        if (log)
        {
            fprintf(log, "RETURN: OS_FS_ERR_PATH_TOO_LONG (VirtPathLen >= OS_MAX_PATH_LEN)\n");
            fclose(log);
        }
        return OS_FS_ERR_PATH_TOO_LONG;
    }

    /* checks to sees if there is a '/' somewhere in the path */
    name_ptr = strrchr(VirtualPath, '/');
    if (log)
    {
        fprintf(log, "strrchr returned name_ptr=%p\n", (const void *)name_ptr);
        fflush(log);
    }
    if (name_ptr == NULL)
    {
        if (log)
        {
            fprintf(log, "RETURN: OS_FS_ERR_PATH_INVALID (no '/')\n");
            fclose(log);
        }
        return OS_FS_ERR_PATH_INVALID;
    }

    /* strrchr returns a pointer to the last '/' char, so we advance one char */
    name_ptr = name_ptr + 1;
    if (memchr(name_ptr, 0, OS_MAX_FILE_NAME) == NULL)
    {
        if (log)
        {
            fprintf(log, "RETURN: OS_FS_ERR_NAME_TOO_LONG (no terminating NUL within OS_MAX_FILE_NAME)\n");
            fclose(log);
        }
        return OS_FS_ERR_NAME_TOO_LONG;
    }

    SysMountPointLen = 0;
    VirtPathBegin    = VirtPathLen; /* <-- initial assignment under scrutiny */
    if (log)
    {
        fprintf(log, "After init: SysMountPointLen=%zu VirtPathBegin(initial)=%zu\n", SysMountPointLen, VirtPathBegin);
        fflush(log);
    }

    /* All valid Virtual paths must start with a '/' character */
    if (VirtualPath[0] != '/')
    {
        if (log)
        {
            fprintf(log, "RETURN: OS_FS_ERR_PATH_INVALID (does not start with '/')\n");
            fclose(log);
        }
        return OS_FS_ERR_PATH_INVALID;
    }

    /* Get a reference lock, as a filesystem check could take some time. */
    return_code = OS_ObjectIdGetBySearch(OS_LOCK_MODE_GLOBAL, LOCAL_OBJID_TYPE, OS_FileSys_FindVirtMountPoint,
                                         (void *)VirtualPath, &token);

    if (log)
    {
        fprintf(log, "After OS_ObjectIdGetBySearch: return_code=%ld token=%p\n", (long)return_code, (void *)&token);
        fflush(log);
    }

    if (return_code != OS_SUCCESS)
    {
        return_code = OS_FS_ERR_PATH_INVALID;
        if (log)
        {
            fprintf(log, "Branch: ObjectIdGetBySearch failed -> return_code set to OS_FS_ERR_PATH_INVALID\n");
            fflush(log);
            fclose(log);
        }
    }
    else
    {
        filesys = OS_OBJECT_TABLE_GET(OS_filesys_table, token);

        if (log)
        {
            fprintf(log, "Found filesys entry at %p flags=0x%08x\n", (void *)filesys, (unsigned)filesys->flags);
            fflush(log);
        }

        if ((filesys->flags & OS_FILESYS_FLAG_IS_MOUNTED_SYSTEM) != 0)
        {
            SysMountPointLen = OS_strnlen(filesys->system_mountpt, sizeof(filesys->system_mountpt));
            VirtPathBegin    = OS_strnlen(filesys->virtual_mountpt, sizeof(filesys->virtual_mountpt));
            if (log)
            {
                fprintf(log, "Mounted system: SysMountPointLen=%zu VirtPathBegin(overwritten)=%zu system_mountpt='%s' virtual_mountpt='%s'\n",
                        SysMountPointLen, VirtPathBegin, filesys->system_mountpt, filesys->virtual_mountpt);
                fflush(log);
            }
            if (SysMountPointLen < OS_MAX_LOCAL_PATH_LEN)
            {
                memcpy(LocalPath, filesys->system_mountpt, SysMountPointLen);
            }
        }
        else
        {
            return_code = OS_ERR_INCORRECT_OBJ_STATE;
            if (log)
            {
                fprintf(log, "Branch: filesys not mounted system -> return_code=OS_ERR_INCORRECT_OBJ_STATE\n");
                fflush(log);
            }
        }

        OS_ObjectIdRelease(&token);
        if (log)
        {
            fprintf(log, "After OS_ObjectIdRelease: current return_code=%ld SysMountPointLen=%zu VirtPathBegin=%zu\n",
                    (long)return_code, SysMountPointLen, VirtPathBegin);
            fflush(log);
        }
    }

    if (return_code == OS_SUCCESS)
    {
        if (log)
        {
            fprintf(log, "Entering final success block: VirtPathLen=%zu VirtPathBegin=%zu SysMountPointLen=%zu\n",
                    VirtPathLen, VirtPathBegin, SysMountPointLen);
            fflush(log);
        }

        if (VirtPathLen < VirtPathBegin)
        {
            return_code = OS_FS_ERR_PATH_INVALID;
            if (log)
            {
                fprintf(log, "RETURN: OS_FS_ERR_PATH_INVALID (VirtPathLen < VirtPathBegin)\n");
                fflush(log);
                fclose(log);
            }
        }
        else
        {
            VirtPathLen -= VirtPathBegin;
            if (log)
            {
                fprintf(log, "After subtract: new VirtPathLen=%zu (length of tail to copy)\n", VirtPathLen);
                fflush(log);
            }
            if ((SysMountPointLen + VirtPathLen) < OS_MAX_LOCAL_PATH_LEN)
            {
                memcpy(&LocalPath[SysMountPointLen], &VirtualPath[VirtPathBegin], VirtPathLen);
                LocalPath[SysMountPointLen + VirtPathLen] = 0;
                if (log)
                {
                    fprintf(log, "Copied to LocalPath: SysMountPointLen=%zu VirtPathBegin=%zu final LocalPath='%s'\n",
                            SysMountPointLen, VirtPathBegin, LocalPath);
                    fflush(log);
                    fclose(log);
                }
            }
            else
            {
                return_code = OS_FS_ERR_PATH_TOO_LONG;
                if (log)
                {
                    fprintf(log, "RETURN: OS_FS_ERR_PATH_TOO_LONG (combined length >= OS_MAX_LOCAL_PATH_LEN)\n");
                    fflush(log);
                    fclose(log);
                }
            }
        }
    }
    else
    {
        /* Not success; log the eventual return. */
        if (log)
        {
            fprintf(log, "Final RETURN (error path): return_code=%ld SysMountPointLen=%zu VirtPathBegin=%zu VirtPathLen=%zu\n",
                    (long)return_code, SysMountPointLen, VirtPathBegin, VirtPathLen);
            fflush(log);
            fclose(log);
        }
    }

    return return_code;
}
