int32 OS_TranslatePath(const char *VirtualPath, char *LocalPath)
{
    OS_object_token_t             token;
    int32                         return_code;
    const char *                  name_ptr;
    OS_filesys_internal_record_t *filesys;
    size_t                        SysMountPointLen;
    size_t                        VirtPathLen;
    size_t                        VirtPathBegin;

    FILE *logf = NULL;

    /*
    ** Check to see if the path pointers are NULL
    */
    /* Check parameters */
    OS_CHECK_POINTER(VirtualPath);
    OS_CHECK_POINTER(LocalPath);

    /*
    ** Check length
    */
    VirtPathLen = OS_strnlen(VirtualPath, OS_MAX_PATH_LEN);

    /* Open log file as soon as we have a valid VirtualPath length to report
     * (This avoids attempting to dereference a NULL VirtualPath). */
    logf = fopen("/tmp/logs/1715.txt", "a");
    if (logf != NULL)
    {
        fprintf(logf, "OS_TranslatePath: entered. VirtualPath=%p, VirtPathLen=%zu\n", (void *)VirtualPath, VirtPathLen);
        /* print a truncated sample of the virtual path for context */
        if (VirtPathLen > 0)
        {
            size_t sample = (VirtPathLen < 128) ? VirtPathLen : 128;
            fprintf(logf, "  VirtualPath sample='%.*s'\n", (int)sample, VirtualPath);
        }
    }

    if (VirtPathLen >= OS_MAX_PATH_LEN)
    {
        if (logf != NULL)
        {
            fprintf(logf, "OS_TranslatePath: path too long (len=%zu, max=%d) -> returning OS_FS_ERR_PATH_TOO_LONG\n", VirtPathLen, OS_MAX_PATH_LEN);
            fclose(logf);
        }
        return OS_FS_ERR_PATH_TOO_LONG;
    }

    /* checks to sees if there is a '/' somewhere in the path */
    name_ptr = strrchr(VirtualPath, '/');
    if (name_ptr == NULL)
    {
        if (logf != NULL)
        {
            fprintf(logf, "OS_TranslatePath: no '/' found in VirtualPath -> returning OS_FS_ERR_PATH_INVALID\n");
            fclose(logf);
        }
        return OS_FS_ERR_PATH_INVALID;
    }

    /* strrchr returns a pointer to the last '/' char, so we advance one char */
    name_ptr = name_ptr + 1;
    if (memchr(name_ptr, 0, OS_MAX_FILE_NAME) == NULL)
    {
        if (logf != NULL)
        {
            fprintf(logf, "OS_TranslatePath: filename component too long -> returning OS_FS_ERR_NAME_TOO_LONG\n");
            fclose(logf);
        }
        return OS_FS_ERR_NAME_TOO_LONG;
    }

    SysMountPointLen = 0;
    VirtPathBegin    = VirtPathLen;

    /*
    ** All valid Virtual paths must start with a '/' character
    */
    if (VirtualPath[0] != '/')
    {
        if (logf != NULL)
        {
            fprintf(logf, "OS_TranslatePath: VirtualPath does not start with '/' -> returning OS_FS_ERR_PATH_INVALID\n");
            fclose(logf);
        }
        return OS_FS_ERR_PATH_INVALID;
    }

    /* Get a reference lock, as a filesystem check could take some time. */
    if (logf != NULL)
    {
        fprintf(logf, "OS_TranslatePath: calling OS_ObjectIdGetBySearch with (void*) cast of const VirtualPath (%p).\n", (void *)VirtualPath);
        fprintf(logf, "  NOTE: This documents the cast-away-const usage that can lead to MISRA Rule 8.13 violations.\n");
        fflush(logf);
    }

    return_code = OS_ObjectIdGetBySearch(OS_LOCK_MODE_GLOBAL, LOCAL_OBJID_TYPE, OS_FileSys_FindVirtMountPoint,
                                         (void *)VirtualPath, &token);

    if (logf != NULL)
    {
        fprintf(logf, "OS_TranslatePath: OS_ObjectIdGetBySearch returned %d\n", return_code);
    }

    if (return_code != OS_SUCCESS)
    {
        return_code = OS_FS_ERR_PATH_INVALID;
        if (logf != NULL)
        {
            fprintf(logf, "OS_TranslatePath: no matching filesystem found -> returning OS_FS_ERR_PATH_INVALID\n");
            fclose(logf);
        }
    }
    else
    {
        filesys = OS_OBJECT_TABLE_GET(OS_filesys_table, token);

        if (logf != NULL)
        {
            fprintf(logf, "OS_TranslatePath: found filesys record at %p\n", (void *)filesys);
            if (filesys != NULL)
            {
                fprintf(logf, "  filesys->flags=0x%08x\n", (unsigned int)filesys->flags);
                fprintf(logf, "  filesys->virtual_mountpt='%.*s'\n", (int)sizeof(filesys->virtual_mountpt), filesys->virtual_mountpt);
                fprintf(logf, "  filesys->system_mountpt='%.*s'\n", (int)sizeof(filesys->system_mountpt), filesys->system_mountpt);
            }
            fflush(logf);
        }

        if ((filesys->flags & OS_FILESYS_FLAG_IS_MOUNTED_SYSTEM) != 0)
        {
            SysMountPointLen = OS_strnlen(filesys->system_mountpt, sizeof(filesys->system_mountpt));
            VirtPathBegin    = OS_strnlen(filesys->virtual_mountpt, sizeof(filesys->virtual_mountpt));
            if (SysMountPointLen < OS_MAX_LOCAL_PATH_LEN)
            {
                memcpy(LocalPath, filesys->system_mountpt, SysMountPointLen);
            }
        }
        else
        {
            return_code = OS_ERR_INCORRECT_OBJ_STATE;
            if (logf != NULL)
            {
                fprintf(logf, "OS_TranslatePath: filesystem not mounted as system -> returning OS_ERR_INCORRECT_OBJ_STATE\n");
                fclose(logf);
            }
        }

        OS_ObjectIdRelease(&token);

        if (logf != NULL && return_code == OS_SUCCESS)
        {
            fprintf(logf, "OS_TranslatePath: after object handling: SysMountPointLen=%zu, VirtPathBegin=%zu\n", SysMountPointLen, VirtPathBegin);
            fflush(logf);
        }
    }

    if (return_code == OS_SUCCESS)
    {
        if (VirtPathLen < VirtPathBegin)
        {
            return_code = OS_FS_ERR_PATH_INVALID;
            if (logf != NULL)
            {
                fprintf(logf, "OS_TranslatePath: VirtPathLen < VirtPathBegin -> returning OS_FS_ERR_PATH_INVALID\n");
                fclose(logf);
            }
        }
        else
        {
            VirtPathLen -= VirtPathBegin;
            if ((SysMountPointLen + VirtPathLen) < OS_MAX_LOCAL_PATH_LEN)
            {
                memcpy(&LocalPath[SysMountPointLen], &VirtualPath[VirtPathBegin], VirtPathLen);
                LocalPath[SysMountPointLen + VirtPathLen] = 0;

                if (logf != NULL)
                {
                    fprintf(logf, "OS_TranslatePath: composed LocalPath (len=%zu) = '%s'\n", SysMountPointLen + VirtPathLen, LocalPath);
                    fclose(logf);
                }
            }
            else
            {
                return_code = OS_FS_ERR_PATH_TOO_LONG;
                if (logf != NULL)
                {
                    fprintf(logf, "OS_TranslatePath: resulting local path too long -> returning OS_FS_ERR_PATH_TOO_LONG\n");
                    fclose(logf);
                }
            }
        }
    }
    else
    {
        /* If we get here and haven't closed the log file yet, close it. */
        if (logf != NULL && !feof(logf))
        {
            fclose(logf);
        }
    }

    return return_code;
}
