int32 OS_TranslatePath(const char *VirtualPath, char *LocalPath)
{
    OS_object_token_t             token;
    int32                         return_code;
    const char *                  name_ptr;
    OS_filesys_internal_record_t *filesys;
    size_t                        SysMountPointLen;
    size_t                        VirtPathLen;
    size_t                        VirtPathBegin;
    FILE *                        logfp = NULL;
    size_t                        vlen_for_log = 0;

    /* Open log file as early as possible to capture entry and any early returns */
    logfp = fopen("/tmp/logs/1715.txt", "a");
    if (logfp != NULL)
    {
        fprintf(logfp, "ENTER OS_TranslatePath: VirtualPath=%p LocalPath=%p\n", (const void *)VirtualPath, (void *)LocalPath);
        if (VirtualPath != NULL)
        {
            vlen_for_log = OS_strnlen(VirtualPath, OS_MAX_PATH_LEN);
            fprintf(logfp, "  VirtualPath (len=%zu): %.*s\n", vlen_for_log, (int)vlen_for_log, VirtualPath);
        }
        else
        {
            fprintf(logfp, "  VirtualPath is NULL\n");
        }
        fflush(logfp);
    }

    /* Check to see if the path pointers are NULL */
    /* Check parameters */
    OS_CHECK_POINTER(VirtualPath);
    OS_CHECK_POINTER(LocalPath);

    /* Check length */
    VirtPathLen = OS_strnlen(VirtualPath, OS_MAX_PATH_LEN);
    if (VirtPathLen >= OS_MAX_PATH_LEN)
    {
        if (logfp != NULL)
        {
            fprintf(logfp, "  EARLY RETURN: OS_FS_ERR_PATH_TOO_LONG (VirtPathLen=%zu)\n", VirtPathLen);
            fclose(logfp);
        }
        return OS_FS_ERR_PATH_TOO_LONG;
    }

    /* checks to sees if there is a '/' somewhere in the path */
    name_ptr = strrchr(VirtualPath, '/');
    if (name_ptr == NULL)
    {
        if (logfp != NULL)
        {
            fprintf(logfp, "  EARLY RETURN: OS_FS_ERR_PATH_INVALID (no '/')\n");
            fclose(logfp);
        }
        return OS_FS_ERR_PATH_INVALID;
    }

    /* strrchr returns a pointer to the last '/' char, so we advance one char */
    name_ptr = name_ptr + 1;
    if (memchr(name_ptr, 0, OS_MAX_FILE_NAME) == NULL)
    {
        if (logfp != NULL)
        {
            fprintf(logfp, "  EARLY RETURN: OS_FS_ERR_NAME_TOO_LONG\n");
            fclose(logfp);
        }
        return OS_FS_ERR_NAME_TOO_LONG;
    }

    SysMountPointLen = 0;
    VirtPathBegin    = VirtPathLen;

    /* All valid Virtual paths must start with a '/' character */
    if (VirtualPath[0] != '/')
    {
        if (logfp != NULL)
        {
            fprintf(logfp, "  EARLY RETURN: OS_FS_ERR_PATH_INVALID (does not start with '/')\n");
            fclose(logfp);
        }
        return OS_FS_ERR_PATH_INVALID;
    }

    /* Get a reference lock, as a filesystem check could take some time. */
    if (logfp != NULL)
    {
        /* Log that a cast will occur here (the reported violation spot) */
        fprintf(logfp, "  Calling OS_ObjectIdGetBySearch with SearchFunc=OS_FileSys_FindVirtMountPoint and arg=(void *)VirtualPath (cast address=%p)\n",
                (const void *)VirtualPath);
        fflush(logfp);
    }

    return_code = OS_ObjectIdGetBySearch(OS_LOCK_MODE_GLOBAL, LOCAL_OBJID_TYPE, OS_FileSys_FindVirtMountPoint,
                                         (void *)VirtualPath, &token);

    if (logfp != NULL)
    {
        fprintf(logfp, "  OS_ObjectIdGetBySearch returned %ld\n", (long)return_code);
        fflush(logfp);
    }

    if (return_code != OS_SUCCESS)
    {
        return_code = OS_FS_ERR_PATH_INVALID;
    }
    else
    {
        filesys = OS_OBJECT_TABLE_GET(OS_filesys_table, token);

        if (logfp != NULL)
        {
            fprintf(logfp, "  filesys ptr=%p flags=0x%08x\n", (const void *)filesys, (unsigned)filesys->flags);
            fflush(logfp);
        }

        if ((filesys->flags & OS_FILESYS_FLAG_IS_MOUNTED_SYSTEM) != 0)
        {
            SysMountPointLen = OS_strnlen(filesys->system_mountpt, sizeof(filesys->system_mountpt));
            VirtPathBegin    = OS_strnlen(filesys->virtual_mountpt, sizeof(filesys->virtual_mountpt));
            if (logfp != NULL)
            {
                fprintf(logfp, "  system_mountpt='%.*s' (len=%zu) virtual_mountpt='%.*s' (len=%zu)\n",
                        (int)SysMountPointLen, filesys->system_mountpt, SysMountPointLen,
                        (int)VirtPathBegin, filesys->virtual_mountpt, VirtPathBegin);
                fflush(logfp);
            }

            if (SysMountPointLen < OS_MAX_LOCAL_PATH_LEN)
            {
                memcpy(LocalPath, filesys->system_mountpt, SysMountPointLen);
                if (logfp != NULL)
                {
                    fprintf(logfp, "  Copied system mount point into LocalPath (len=%zu)\n", SysMountPointLen);
                    fflush(logfp);
                }
            }
        }
        else
        {
            return_code = OS_ERR_INCORRECT_OBJ_STATE;
            if (logfp != NULL)
            {
                fprintf(logfp, "  filesys not mounted system -> OS_ERR_INCORRECT_OBJ_STATE\n");
                fflush(logfp);
            }
        }

        OS_ObjectIdRelease(&token);
        if (logfp != NULL)
        {
            fprintf(logfp, "  Released object token\n");
            fflush(logfp);
        }
    }

    if (return_code == OS_SUCCESS)
    {
        if (VirtPathLen < VirtPathBegin)
        {
            if (logfp != NULL)
            {
                fprintf(logfp, "  EARLY RETURN: OS_FS_ERR_PATH_INVALID (VirtPathLen=%zu < VirtPathBegin=%zu)\n", VirtPathLen, VirtPathBegin);
                fclose(logfp);
            }
            return OS_FS_ERR_PATH_INVALID;
        }
        else
        {
            VirtPathLen -= VirtPathBegin;
            if ((SysMountPointLen + VirtPathLen) < OS_MAX_LOCAL_PATH_LEN)
            {
                memcpy(&LocalPath[SysMountPointLen], &VirtualPath[VirtPathBegin], VirtPathLen);
                LocalPath[SysMountPointLen + VirtPathLen] = 0;
                if (logfp != NULL)
                {
                    fprintf(logfp, "  Built LocalPath='%s' (total_len=%zu)\n", LocalPath, SysMountPointLen + VirtPathLen);
                    fflush(logfp);
                }
            }
            else
            {
                if (logfp != NULL)
                {
                    fprintf(logfp, "  EARLY RETURN: OS_FS_ERR_PATH_TOO_LONG (SysMountPointLen=%zu VirtPathLen=%zu)\n",
                            SysMountPointLen, VirtPathLen);
                    fclose(logfp);
                }
                return OS_FS_ERR_PATH_TOO_LONG;
            }
        }
    }

    if (logfp != NULL)
    {
        fprintf(logfp, "EXIT OS_TranslatePath with return_code=%ld\n", (long)return_code);
        fclose(logfp);
    }

    return return_code;
}
