void OS_Unlock_Global_Impl(osal_objtype_t idtype)
{
    OS_impl_objtype_lock_t *impl;
    int                     ret;

    /* Probe: log entry and idtype */
    {
        FILE *logf = fopen("/tmp/logs/3038.txt", "a");
        if (logf != NULL)
        {
            fprintf(logf, "OS_Unlock_Global_Impl: entered (idtype=%d)\n", (int)idtype);
            fclose(logf);
        }
    }

    impl = OS_impl_objtype_lock_table[idtype];

    if (impl != NULL)
    {
        /* Notify any waiting threads that the state _may_ have changed */
        ret = pthread_cond_broadcast(&impl->cond);
        if (ret != 0)
        {
            /* Log the error to a file so that tests can observe that this branch was taken
               and capture the return code and strerror text. This makes the branch observable
               even if OS_DEBUG is an empty macro. */
            FILE *logf = fopen("/tmp/logs/3038.txt", "a");
            if (logf != NULL)
            {
                fprintf(logf, "OS_Unlock_Global_Impl: pthread_cond_broadcast failed: ret=%d, strerror=%s\n",
                        ret, strerror(ret));
                fclose(logf);
            }

            /* Preserve original debug call (may be a no-op) */
            OS_DEBUG("pthread_cond_broadcast(&impl->cond): %s", strerror(ret));
            /* unexpected but keep going (not critical) */
        }

        ret = pthread_mutex_unlock(&impl->mutex);
        if (ret != 0)
        {
            /* Log the mutex unlock error as well */
            FILE *logf = fopen("/tmp/logs/3038.txt", "a");
            if (logf != NULL)
            {
                fprintf(logf, "OS_Unlock_Global_Impl: pthread_mutex_unlock failed: ret=%d, strerror=%s\n",
                        ret, strerror(ret));
                fclose(logf);
            }

            OS_DEBUG("pthread_mutex_unlock(&impl->mutex): %s", strerror(ret));
        }
    }
    else
    {
        /* Log the case where there is no impl for the given idtype */
        FILE *logf = fopen("/tmp/logs/3038.txt", "a");
        if (logf != NULL)
        {
            fprintf(logf, "OS_Unlock_Global_Impl: no impl (idtype=%d)\n", (int)idtype);
            fclose(logf);
        }
    }
}
