int32 OS_chkfs(const char *name, bool repair)
{
    OS_object_token_t token;
    int32             return_code;
    FILE *log = NULL;

    /* Open log file for append; best-effort. */
    log = fopen("/tmp/logs/840.txt", "a");
    if (log)
    {
        fprintf(log, "OS_chkfs ENTRY: name=%s, repair=%d\n", (name != NULL) ? name : "(null)", (int)repair);
        fflush(log);
    }

    /* Check parameters */
    OS_CHECK_PATHNAME(name);

    /* Get a reference lock, as a filesystem check could take some time. */
    return_code = OS_ObjectIdGetBySearch(OS_LOCK_MODE_REFCOUNT, LOCAL_OBJID_TYPE, OS_FileSys_FindVirtMountPoint,
                                         (void *)name, &token);

    if (log)
    {
        fprintf(log, "OS_chkfs after ObjectIdGetBySearch: return_code=%d, token_addr=%p\n", return_code, (void *)&token);
        fflush(log);
    }

    if (return_code == OS_SUCCESS)
    {
        if (log)
        {
            fprintf(log, "OS_chkfs calling OS_FileSysCheckVolume_Impl with repair=%d\n", (int)repair);
            fflush(log);
        }

        return_code = OS_FileSysCheckVolume_Impl(&token, repair);

        if (log)
        {
            fprintf(log, "OS_FileSysCheckVolume_Impl returned %d\n", return_code);
            fflush(log);
        }

        OS_ObjectIdRelease(&token);

        if (log)
        {
            fprintf(log, "OS_ObjectIdRelease called\n");
            fflush(log);
        }
    }
    else
    {
        if (log)
        {
            fprintf(log, "OS_chkfs early exit with return_code=%d\n", return_code);
            fflush(log);
        }
    }

    if (log)
    {
        fprintf(log, "OS_chkfs EXIT: returning %d\n", return_code);
        fclose(log);
    }

    return return_code;
}
