int32 OS_chkfs(const char *name, bool repair)
{
    OS_object_token_t token;
    int32             return_code;
    FILE *log = fopen("/tmp/logs/843.txt", "a");

    /* Minimal entry probe: record that the function was entered and the name pointer. */
    if (log != NULL)
    {
        fprintf(log, "OS_chkfs ENTER: name=%p, repair=%d\n", (const void *)name, (int)repair);
        fflush(log);
        fclose(log);
    }

    /* Check parameters */
    OS_CHECK_PATHNAME(name);

    /* Get a reference lock, as a filesystem check could take some time. */
    return_code = OS_ObjectIdGetBySearch(OS_LOCK_MODE_REFCOUNT, LOCAL_OBJID_TYPE, OS_FileSys_FindVirtMountPoint,
                                         (void *)name, &token);

    /* Log the result of the search call so callers can see whether the search succeeded and
       what token (address) was produced. This helps to correlate the call that triggers
       the downstream string-compare behavior inside the search helper. */
    log = fopen("/tmp/logs/843.txt", "a");
    if (log != NULL)
    {
        fprintf(log, "OS_chkfs: OS_ObjectIdGetBySearch returned %ld", (long)return_code);
        if (return_code == OS_SUCCESS)
        {
            /* token is an opaque object; log its address to correlate with later release */
            fprintf(log, ", token_addr=%p\n", (void *)&token);
        }
        else
        {
            fprintf(log, "\n");
        }
        fflush(log);
        fclose(log);
    }

    if (return_code == OS_SUCCESS)
    {
        /* Log before calling the check implementation */
        log = fopen("/tmp/logs/843.txt", "a");
        if (log != NULL)
        {
            fprintf(log, "OS_chkfs: calling OS_FileSysCheckVolume_Impl with token=%p\n", (void *)&token);
            fflush(log);
            fclose(log);
        }

        return_code = OS_FileSysCheckVolume_Impl(&token, repair);

        /* Log the return from the check implementation */
        log = fopen("/tmp/logs/843.txt", "a");
        if (log != NULL)
        {
            fprintf(log, "OS_chkfs: OS_FileSysCheckVolume_Impl returned %ld\n", (long)return_code);
            fflush(log);
            fclose(log);
        }

        OS_ObjectIdRelease(&token);

        /* Log that we released the token */
        log = fopen("/tmp/logs/843.txt", "a");
        if (log != NULL)
        {
            fprintf(log, "OS_chkfs: released token=%p\n", (void *)&token);
            fflush(log);
            fclose(log);
        }
    }

    /* Final return (same behavior as original). */
    return return_code;
}
