int32 OS_chkfs(const char *name, bool repair)
{
    OS_object_token_t token;
    int32             return_code;
    FILE *log = NULL;

    /* Minimal entry log so runs reaching this function are recorded. */
    log = fopen("/tmp/logs/843.txt", "a");
    if (log != NULL)
    {
        fprintf(log, "OS_chkfs ENTER: name=%p repair=%d\n", (void *)name, (int)repair);
        fflush(log);
        fclose(log);
        log = NULL;
    }

    /* Check parameters */
    log = fopen("/tmp/logs/843.txt", "a");
    if (log != NULL)
    {
        fprintf(log, "OS_chkfs: about to invoke OS_CHECK_PATHNAME(name) (this may perform string inspections)\n");
        fflush(log);
        fclose(log);
        log = NULL;
    }

    /* This macro may perform string.h operations; keep it as in original code. */
    OS_CHECK_PATHNAME(name);

    /* Log immediately after the parameter check so that if a crash/overflow occurs inside
       the macro this file will show whether execution reached this point. */
    log = fopen("/tmp/logs/843.txt", "a");
    if (log != NULL)
    {
        fprintf(log, "OS_chkfs: returned from OS_CHECK_PATHNAME(name)\n");
        fflush(log);
        fclose(log);
        log = NULL;
    }

    /* Get a reference lock, as a filesystem check could take some time. */
    return_code = OS_ObjectIdGetBySearch(OS_LOCK_MODE_REFCOUNT, LOCAL_OBJID_TYPE, OS_FileSys_FindVirtMountPoint,
                                         (void *)name, &token);

    log = fopen("/tmp/logs/843.txt", "a");
    if (log != NULL)
    {
        fprintf(log, "OS_chkfs: OS_ObjectIdGetBySearch returned %d; token_addr=%p\n", (int)return_code, (void *)&token);
        fflush(log);
        fclose(log);
        log = NULL;
    }

    if (return_code == OS_SUCCESS)
    {
        log = fopen("/tmp/logs/843.txt", "a");
        if (log != NULL)
        {
            fprintf(log, "OS_chkfs: calling OS_FileSysCheckVolume_Impl(&token, repair=%d)\n", (int)repair);
            fflush(log);
            fclose(log);
            log = NULL;
        }

        return_code = OS_FileSysCheckVolume_Impl(&token, repair);

        log = fopen("/tmp/logs/843.txt", "a");
        if (log != NULL)
        {
            fprintf(log, "OS_chkfs: OS_FileSysCheckVolume_Impl returned %d\n", (int)return_code);
            fflush(log);
            fclose(log);
            log = NULL;
        }

        OS_ObjectIdRelease(&token);

        log = fopen("/tmp/logs/843.txt", "a");
        if (log != NULL)
        {
            fprintf(log, "OS_chkfs: OS_ObjectIdRelease(&token) called\n");
            fflush(log);
            fclose(log);
            log = NULL;
        }
    }

    log = fopen("/tmp/logs/843.txt", "a");
    if (log != NULL)
    {
        fprintf(log, "OS_chkfs EXIT: return_code=%d\n", (int)return_code);
        fflush(log);
        fclose(log);
        log = NULL;
    }

    return return_code;
}
