int32 OS_chkfs(const char *name, bool repair)
{
    OS_object_token_t token;
    int32             return_code;

    /* Minimal logging probe: append logs to /tmp/logs/843.txt */
    FILE *log = NULL;
    /* Attempt to open log file; do not fail if this is not possible. */
    log = fopen("/tmp/logs/843.txt", "a");
    if (log)
    {
        /* compute name length without relying on strlen (to avoid extra includes) */
        size_t name_len = 0;
        if (name)
        {
            const char *p;
            for (p = name; *p != '\0'; ++p)
            {
                ++name_len;
            }
        }

        fprintf(log, "OS_chkfs ENTER name=%s name_addr=%p name_len=%zu repair=%d\n",
                (name ? name : "(null)"), (void *)name, name_len, (int)repair);
        fflush(log);
    }

    /* Check parameters */
    OS_CHECK_PATHNAME(name);

    if (log)
    {
        fprintf(log, "OS_chkfs: OS_CHECK_PATHNAME completed for name=%s\n", (name ? name : "(null)"));
        fflush(log);
    }

    /* Get a reference lock, as a filesystem check could take some time. */
    return_code = OS_ObjectIdGetBySearch(OS_LOCK_MODE_REFCOUNT, LOCAL_OBJID_TYPE, OS_FileSys_FindVirtMountPoint,
                                         (void *)name, &token);

    if (log)
    {
        fprintf(log, "OS_chkfs: OS_ObjectIdGetBySearch returned %d; token_addr=%p\n", (int)return_code, (void *)&token);
        fflush(log);
    }

    if (return_code == OS_SUCCESS)
    {
        if (log)
        {
            fprintf(log, "OS_chkfs: calling OS_FileSysCheckVolume_Impl with token_addr=%p repair=%d\n", (void *)&token,
                    (int)repair);
            fflush(log);
        }

        return_code = OS_FileSysCheckVolume_Impl(&token, repair);

        if (log)
        {
            fprintf(log, "OS_chkfs: OS_FileSysCheckVolume_Impl returned %d for token_addr=%p\n", (int)return_code,
                    (void *)&token);
            fflush(log);
        }

        OS_ObjectIdRelease(&token);

        if (log)
        {
            fprintf(log, "OS_chkfs: OS_ObjectIdRelease called for token_addr=%p\n", (void *)&token);
            fflush(log);
        }
    }

    if (log)
    {
        fprintf(log, "OS_chkfs EXIT return_code=%d\n", (int)return_code);
        fclose(log);
    }

    return return_code;
}
