int32 OS_mkdir(const char *path, uint32 access)
{
    int32 return_code;
    char  local_path[OS_MAX_LOCAL_PATH_LEN];

    /* Open log file for append. Best-effort logging; do not change program flow on failure. */
    FILE *log = fopen("/tmp/logs/1038.txt", "a");
    if (log != NULL)
    {
        fprintf(log, "OS_mkdir: ENTER path=\"%s\", access=%u\n", path ? path : "(null)", (unsigned)access);
        fflush(log);
    }

    return_code = OS_TranslatePath(path, local_path);

    if (log != NULL)
    {
        /* log the result of path translation and the translated path */
        fprintf(log, "OS_mkdir: OS_TranslatePath returned %d, local_path=\"%s\"\n", return_code, local_path);
        fflush(log);
    }

    if (return_code == OS_SUCCESS)
    {
        /* Log that we are about to call the implementation-level directory create */
        if (log != NULL)
        {
            fprintf(log, "OS_mkdir: calling OS_DirCreate_Impl(local_path=\"%s\", access=%u)\n", local_path, (unsigned)access);
            fflush(log);
        }

        return_code = OS_DirCreate_Impl(local_path, access);

        if (log != NULL)
        {
            fprintf(log, "OS_mkdir: OS_DirCreate_Impl returned %d\n", return_code);
            fflush(log);
        }
    }
    else
    {
        if (log != NULL)
        {
            fprintf(log, "OS_mkdir: Skipping OS_DirCreate_Impl due to OS_TranslatePath failure (%d)\n", return_code);
            fflush(log);
        }
    }

    if (log != NULL)
    {
        fprintf(log, "OS_mkdir: EXIT return_code=%d\n", return_code);
        fclose(log);
    }

    return return_code;
}
