int32 OS_mkdir(const char *path, uint32 access)
{
    int32 return_code;
    char  local_path[OS_MAX_LOCAL_PATH_LEN];

    /* Log entry into OS_mkdir with the provided parameters. */
    {
        FILE *log = fopen("/tmp/logs/1038.txt", "a");
        if (log)
        {
            fprintf(log, "OS_mkdir ENTRY: path=\"%s\", access=%u\n",
                    path ? path : "(null)", (unsigned)access);
            fclose(log);
        }
    }

    /* Preserve original logic: translate the path first. */
    return_code = OS_TranslatePath(path, local_path);

    /* Log result of translation and the translated path when available. */
    {
        FILE *log = fopen("/tmp/logs/1038.txt", "a");
        if (log)
        {
            fprintf(log, "OS_mkdir: OS_TranslatePath returned %ld\n", (long)return_code);
            if (return_code == OS_SUCCESS)
            {
                /* local_path is expected to be a NUL-terminated string on success. */
                fprintf(log, "OS_mkdir: translated local_path=\"%s\"\n", local_path);
            }
            fclose(log);
        }
    }

    if (return_code == OS_SUCCESS)
    {
        /* Log about to call the implementation creating the directory. */
        {
            FILE *log = fopen("/tmp/logs/1038.txt", "a");
            if (log)
            {
                fprintf(log, "OS_mkdir: calling OS_DirCreate_Impl(local_path=\"%s\", access=%u)\n",
                        local_path, (unsigned)access);
                fclose(log);
            }
        }

        return_code = OS_DirCreate_Impl(local_path, access);

        /* Log result from the implementation call. */
        {
            FILE *log = fopen("/tmp/logs/1038.txt", "a");
            if (log)
            {
                fprintf(log, "OS_mkdir: OS_DirCreate_Impl returned %ld\n", (long)return_code);
                fclose(log);
            }
        }
    }

    /* Final exit log with return code. */
    {
        FILE *log = fopen("/tmp/logs/1038.txt", "a");
        if (log)
        {
            fprintf(log, "OS_mkdir EXIT: returning %ld\n", (long)return_code);
            fclose(log);
        }
    }

    return return_code;
}
