int32 OS_mv(const char *src, const char *dest)
{
#include <stdio.h>
#include <sys/stat.h>

    int32 return_code;
    FILE *logf = NULL;

    /* Attempt to ensure the log directory exists. If it already exists or cannot be
       created, we still proceed with the original logic. This helps capture logs
       in /tmp/logs/1763.txt when possible. */
    (void)mkdir("/tmp/logs", 0777);

    /* Open logfile for append. If this fails, we silently continue so we do not
       change the original behavior of OS_mv. */
    logf = fopen("/tmp/logs/1763.txt", "a");
    if (logf)
    {
        fprintf(logf, "OS_mv ENTER\n");
        fprintf(logf, "  src pointer: %p\n", (const void *)src);
        fprintf(logf, "  dest pointer: %p\n", (const void *)dest);
        fflush(logf);
    }

    /* First try rename - this only works if it is on the same filesystem */
    return_code = OS_rename(src, dest);

    if (logf)
    {
        fprintf(logf, "  OS_rename returned: %ld\n", (long)return_code);
        fflush(logf);
    }

    if (return_code != OS_SUCCESS)
    {
        if (logf)
        {
            fprintf(logf, "  OS_rename failed, attempting OS_cp\n");
            fflush(logf);
        }

        return_code = OS_cp(src, dest);

        if (logf)
        {
            fprintf(logf, "  OS_cp returned: %ld\n", (long)return_code);
            fflush(logf);
        }

        if (return_code == OS_SUCCESS)
        {
            if (logf)
            {
                fprintf(logf, "  OS_cp succeeded, calling OS_remove on src: %p\n", (const void *)src);
                fflush(logf);
            }

            OS_remove(src);

            if (logf)
            {
                fprintf(logf, "  OS_remove called for src\n");
                fflush(logf);
            }
        }
    }

    if (logf)
    {
        fprintf(logf, "OS_mv EXIT, return_code: %ld\n", (long)return_code);
        fclose(logf);
    }

    return return_code;
}
