int32 OS_remove(const char *path)
{
    int32 return_code;
    char  local_path[OS_MAX_LOCAL_PATH_LEN];

    /* Open log file for append. If this fails, continue without logging. */
    FILE *log = fopen("/tmp/logs/1648.txt", "a");
    if (log)
    {
        fprintf(log, "OS_remove: entered; path=%s\n", (path != NULL) ? path : "(null)");
    }

    /* Preserve original behavior: translate the path, then remove if successful. */
    return_code = OS_TranslatePath(path, local_path);

    if (log)
    {
        if (return_code == OS_SUCCESS)
        {
            /* Only print local_path when translation succeeded (it is then valid).
               This avoids printing uninitialized data when translation fails. */
            fprintf(log, "OS_TranslatePath -> OS_SUCCESS; local_path=\"%s\"\n", local_path);
        }
        else
        {
            fprintf(log, "OS_TranslatePath -> %d (failure); local_path not valid\n", return_code);
        }
    }

    if (return_code == OS_SUCCESS)
    {
        if (log)
        {
            fprintf(log, "Calling OS_FileRemove_Impl(local_path=\"%s\")\n", local_path);
        }

        return_code = OS_FileRemove_Impl(local_path);

        if (log)
        {
            fprintf(log, "OS_FileRemove_Impl returned %d\n", return_code);
        }
    }
    else
    {
        if (log)
        {
            fprintf(log, "Skipping OS_FileRemove_Impl due to translate failure\n");
        }
    }

    if (log)
    {
        fprintf(log, "OS_remove: exiting with return_code=%d\n\n", return_code);
        fclose(log);
    }

    return return_code;
}
