int32 OS_rename(const char *old, const char *new)
{
    OS_object_iter_t             iter;
    OS_stream_internal_record_t *stream;
    int32                        return_code;
    char                         old_path[OS_MAX_LOCAL_PATH_LEN];
    char                         new_path[OS_MAX_LOCAL_PATH_LEN];

    /* Open logging file. Use append so multiple calls accumulate evidence. */
    FILE *log = fopen("/tmp/logs/2388.txt", "a");
    if (log)
    {
        fprintf(log, "OS_rename ENTER: old=%p new=%p\n", (const void *)old, (const void *)new);
        fprintf(log, "OS_rename INPUT STRINGS: old_str=%s new_str=%s\n",
                (old ? old : "(null)"), (new ? new : "(null)"));
    }

    return_code = OS_TranslatePath(old, old_path);
    if (log)
    {
        fprintf(log, "After OS_TranslatePath(old): return_code=%ld old_path=%s\n", (long)return_code, (return_code == OS_SUCCESS ? old_path : "(invalid)"));
    }

    if (return_code == OS_SUCCESS)
    {
        return_code = OS_TranslatePath(new, new_path);
        if (log)
        {
            fprintf(log, "After OS_TranslatePath(new): return_code=%ld new_path=%s\n", (long)return_code, (return_code == OS_SUCCESS ? new_path : "(invalid)"));
        }
    }

    if (return_code == OS_SUCCESS)
    {
        return_code = OS_FileRename_Impl(old_path, new_path);
        if (log)
        {
            fprintf(log, "After OS_FileRename_Impl: return_code=%ld\n", (long)return_code);
        }
    }

    if (return_code == OS_SUCCESS)
    {
        if (log)
        {
            fprintf(log, "Entering object table iteration to update open streams.\n");
        }

        OS_ObjectIdIterateActive(LOCAL_OBJID_TYPE, &iter);

        while (OS_ObjectIdIteratorGetNext(&iter))
        {
            stream = OS_OBJECT_TABLE_GET(OS_stream_table, iter.token);

            if (log)
            {
                /* Log key stream state to capture whether the renaming branch is taken */
                fprintf(log, "Iter token=%u socket_domain=%d stream_name=%s\n",
                        (unsigned int)iter.token,
                        (int)stream->socket_domain,
                        stream->stream_name);
            }

            if (stream->socket_domain == OS_SocketDomain_INVALID && strcmp(stream->stream_name, old) == 0)
            {
                if (log)
                {
                    fprintf(log, "Stream match found for token=%u: renaming from '%s' to '%s'\n",
                            (unsigned int)iter.token, stream->stream_name, new);
                }

                strncpy(stream->stream_name, new, sizeof(stream->stream_name) - 1);
                stream->stream_name[sizeof(stream->stream_name) - 1] = 0;

                if (log)
                {
                    fprintf(log, "After rename, stream_name=%s\n", stream->stream_name);
                }
            }
        }

        OS_ObjectIdIteratorDestroy(&iter);

        if (log)
        {
            fprintf(log, "Completed object iteration.\n");
        }
    }

    if (log)
    {
        fprintf(log, "OS_rename EXIT: return_code=%ld\n\n", (long)return_code);
        fclose(log);
    }

    return return_code;
}
