int32 OS_rename(const char *old, const char *new)
{
    OS_object_iter_t             iter;
    OS_stream_internal_record_t *stream;
    int32                        return_code;
    char                         old_path[OS_MAX_LOCAL_PATH_LEN];
    char                         new_path[OS_MAX_LOCAL_PATH_LEN];

    /* Open log file for appending diagnostic information. If this fails, continue without logging. */
    FILE *logf = fopen("/tmp/logs/2388.txt", "a");
    if (logf)
    {
        fprintf(logf, "OS_rename ENTER: old=%s new=%s\n", (old ? old : "(null)"), (new ? new : "(null)"));
        fflush(logf);
    }

    return_code = OS_TranslatePath(old, old_path);
    if (logf)
    {
        fprintf(logf, "After OS_TranslatePath(old): return_code=%d\n", (int)return_code);
        if (return_code == OS_SUCCESS)
        {
            fprintf(logf, "  old_path='%s'\n", old_path);
        }
        fflush(logf);
    }

    if (return_code == OS_SUCCESS)
    {
        return_code = OS_TranslatePath(new, new_path);
        if (logf)
        {
            fprintf(logf, "After OS_TranslatePath(new): return_code=%d\n", (int)return_code);
            if (return_code == OS_SUCCESS)
            {
                fprintf(logf, "  new_path='%s'\n", new_path);
            }
            fflush(logf);
        }
    }

    if (return_code == OS_SUCCESS)
    {
        return_code = OS_FileRename_Impl(old_path, new_path);
        if (logf)
        {
            fprintf(logf, "After OS_FileRename_Impl: return_code=%d\n", (int)return_code);
            fflush(logf);
        }
    }

    if (return_code == OS_SUCCESS)
    {
        OS_ObjectIdIterateActive(LOCAL_OBJID_TYPE, &iter);
        if (logf)
        {
            fprintf(logf, "Iterating open streams to update names (if any)\n");
            fflush(logf);
        }

        while (OS_ObjectIdIteratorGetNext(&iter))
        {
            stream = OS_OBJECT_TABLE_GET(OS_stream_table, iter.token);

            if (logf)
            {
                fprintf(logf, "  Checking token=%u stream_name='%s' socket_domain=%d\n",
                        (unsigned int)iter.token,
                        (stream && stream->stream_name ? stream->stream_name : "(null)"),
                        (int)(stream ? stream->socket_domain : -1));
                fflush(logf);
            }

            if (stream->socket_domain == OS_SocketDomain_INVALID && strcmp(stream->stream_name, old) == 0)
            {
                if (logf)
                {
                    fprintf(logf, "    MATCH FOUND: replacing '%s' with '%s' for token=%u\n",
                            stream->stream_name, (new ? new : "(null)"), (unsigned int)iter.token);
                    fflush(logf);
                }

                strncpy(stream->stream_name, new, sizeof(stream->stream_name) - 1);
                stream->stream_name[sizeof(stream->stream_name) - 1] = 0;

                if (logf)
                {
                    fprintf(logf, "    AFTER REPLACE: stream_name='%s'\n", stream->stream_name);
                    fflush(logf);
                }
            }
        }

        OS_ObjectIdIteratorDestroy(&iter);
        if (logf)
        {
            fprintf(logf, "Finished iterating streams.\n");
            fflush(logf);
        }
    }

    if (logf)
    {
        fprintf(logf, "OS_rename EXIT: return_code=%d\n\n", (int)return_code);
        fclose(logf);
    }

    return return_code;
}
