int32 OS_rename(const char *old, const char *new)
{
    OS_object_iter_t             iter;
    OS_stream_internal_record_t *stream;
    int32                        return_code;
    char                         old_path[OS_MAX_LOCAL_PATH_LEN];
    char                         new_path[OS_MAX_LOCAL_PATH_LEN];

    /* Open log file for append. This records control flow and state to help
     * reproduce the reported Rule 8.13 scenario. */
    FILE *log = fopen("/tmp/logs/2385.txt", "a");
    if (log)
    {
        fprintf(log, "OS_rename entered: old=%p \"%s\" new=%p \"%s\"\n",
                (const void *)old, old ? old : "(null)", (const void *)new, new ? new : "(null)");
    }

    return_code = OS_TranslatePath(old, old_path);
    if (log)
    {
        fprintf(log, "After OS_TranslatePath(old): return_code=%d old_path=\"%s\"\n",
                (int)return_code, (return_code == OS_SUCCESS && old_path[0]) ? old_path : "(unset)");
    }

    if (return_code == OS_SUCCESS)
    {
        return_code = OS_TranslatePath(new, new_path);
        if (log)
        {
            fprintf(log, "After OS_TranslatePath(new): return_code=%d new_path=\"%s\"\n",
                    (int)return_code, (return_code == OS_SUCCESS && new_path[0]) ? new_path : "(unset)");
        }
    }

    if (return_code == OS_SUCCESS)
    {
        return_code = OS_FileRename_Impl(old_path, new_path);
        if (log)
        {
            fprintf(log, "After OS_FileRename_Impl: return_code=%d\n", (int)return_code);
        }
    }

    if (return_code == OS_SUCCESS)
    {
        OS_ObjectIdIterateActive(LOCAL_OBJID_TYPE, &iter);
        if (log)
        {
            fprintf(log, "OS_ObjectIdIterateActive called, begin iteration\n");
        }

        while (OS_ObjectIdIteratorGetNext(&iter))
        {
            stream = OS_OBJECT_TABLE_GET(OS_stream_table, iter.token);

            if (log)
            {
                fprintf(log, "Iterator token=%u stream=%p socket_domain=%d stream_name=\"%s\"\n",
                        (unsigned)iter.token,
                        (void *)stream,
                        (int)stream->socket_domain,
                        (stream && stream->stream_name[0]) ? stream->stream_name : "(empty)");
            }

            if (stream->socket_domain == OS_SocketDomain_INVALID && strcmp(stream->stream_name, old) == 0)
            {
                if (log)
                {
                    fprintf(log, "Match found: updating stream_name from \"%s\" to \"%s\". Addresses: old_name=%p new_name=%p\n",
                            stream->stream_name,
                            new ? new : "(null)",
                            (void *)stream->stream_name,
                            (const void *)new);
                }

                /* Preserve original behavior when copying the new name. */
                strncpy(stream->stream_name, new, sizeof(stream->stream_name) - 1);
                stream->stream_name[sizeof(stream->stream_name) - 1] = 0;
            }
        }

        OS_ObjectIdIteratorDestroy(&iter);
        if (log)
        {
            fprintf(log, "Iterator destroyed\n");
        }
    }

    if (log)
    {
        fprintf(log, "OS_rename exiting: return_code=%d\n\n", (int)return_code);
        fclose(log);
    }

    return return_code;
}
