int32 OS_rmfs(const char *devname)
{
    int32             return_code;
    OS_object_token_t token;

    /* Minimal logging probe to trace control flow and state for Rule 17.2 investigation. */
    {
        /* Attempt to append logs detailing entry and subsequent calls. Use stdio file API. */
        FILE *log = fopen("/tmp/logs/2300.txt", "a");
        if (log != NULL)
        {
            fprintf(log, "ENTER: OS_rmfs devname=%p\n", (void *)devname);
            if (devname != NULL)
            {
                /* Print the string only if non-null to avoid crashes when devname is invalid. */
                fprintf(log, "  devname=\"%s\"\n", devname);
            }
            fclose(log);
        }
    }

    /* Check parameters */
    OS_CHECK_PATHNAME(devname);

    {
        FILE *log = fopen("/tmp/logs/2300.txt", "a");
        if (log != NULL)
        {
            fprintf(log, "After OS_CHECK_PATHNAME\n");
            fclose(log);
        }
    }

    return_code = OS_ObjectIdGetByName(OS_LOCK_MODE_EXCLUSIVE, LOCAL_OBJID_TYPE, devname, &token);

    {
        FILE *log = fopen("/tmp/logs/2300.txt", "a");
        if (log != NULL)
        {
            fprintf(log, "OS_ObjectIdGetByName returned %ld\n", (long)return_code);
            /* Log the token storage address so callers can correlate subsequent calls. */
            fprintf(log, "  token_address=%p\n", (void *)&token);
            fclose(log);
        }
    }

    if (return_code == OS_SUCCESS)
    {
        /*
         * NOTE: It is likely that if the file system is mounted,
         * this call to stop the volume will fail.
         *
         * It would be prudent to first check the flags to ensure that
         * the filesystem is unmounted first, but this would break
         * compatibility with the existing unit tests.
         */

        {
            FILE *log = fopen("/tmp/logs/2300.txt", "a");
            if (log != NULL)
            {
                fprintf(log, "Calling OS_FileSysStopVolume_Impl with token_address=%p\n", (void *)&token);
                fclose(log);
            }
        }

        return_code = OS_FileSysStopVolume_Impl(&token);

        {
            FILE *log = fopen("/tmp/logs/2300.txt", "a");
            if (log != NULL)
            {
                fprintf(log, "OS_FileSysStopVolume_Impl returned %ld\n", (long)return_code);
                fclose(log);
            }
        }

        /* Free the entry in the master table  */
        {
            FILE *log = fopen("/tmp/logs/2300.txt", "a");
            if (log != NULL)
            {
                fprintf(log, "Calling OS_ObjectIdFinalizeDelete with previous_return=%ld token_address=%p\n", (long)return_code, (void *)&token);
                fclose(log);
            }
        }

        return_code = OS_ObjectIdFinalizeDelete(return_code, &token);

        {
            FILE *log = fopen("/tmp/logs/2300.txt", "a");
            if (log != NULL)
            {
                fprintf(log, "OS_ObjectIdFinalizeDelete returned %ld\n", (long)return_code);
                fprintf(log, "EXIT: OS_rmfs returning %ld\n", (long)return_code);
                fclose(log);
            }
        }
    }
    else
    {
        {
            FILE *log = fopen("/tmp/logs/2300.txt", "a");
            if (log != NULL)
            {
                fprintf(log, "OS_ObjectIdGetByName failed; setting return_code=OS_ERR_NAME_NOT_FOUND\n");
                fprintf(log, "EXIT: OS_rmfs returning %ld\n", (long)OS_ERR_NAME_NOT_FOUND);
                fclose(log);
            }
        }

        return_code = OS_ERR_NAME_NOT_FOUND;
    }

    return return_code;
}
