int32 OS_rmfs(const char *devname)
{
    int32             return_code;
    OS_object_token_t token;

    /* Probe logging: capture entry and input state */
    {
        FILE *log = fopen("/tmp/logs/2305.txt", "a");
        if (log != NULL)
        {
            fprintf(log, "OS_rmfs: entered\n");
            fprintf(log, "  devname ptr=%p\n", (const void *)devname);
            if (devname != NULL)
            {
                /* print the provided device name (safely) */
                fprintf(log, "  devname string=\"%s\"\n", devname);
            }
            else
            {
                fprintf(log, "  devname string=(null)\n");
            }
            fflush(log);
            fclose(log);
        }
    }

    /* Check parameters */
    OS_CHECK_PATHNAME(devname);

    return_code = OS_ObjectIdGetByName(OS_LOCK_MODE_EXCLUSIVE, LOCAL_OBJID_TYPE, devname, &token);

    /* Log result of name lookup and token state */
    {
        FILE *log = fopen("/tmp/logs/2305.txt", "a");
        if (log != NULL)
        {
            fprintf(log, "OS_rmfs: after OS_ObjectIdGetByName() -> return_code=%d\n", (int)return_code);

            /* Dump token bytes (raw) to help correlate with downstream calls that may cast away const */
            {
                int i;
                unsigned char *ptr = (unsigned char *)&token;
                int tok_size = (int)sizeof(token);

                fprintf(log, "  token address=%p size=%d bytes\n", (void *)&token, tok_size);
                fprintf(log, "  token raw=");
                for (i = 0; i < tok_size; ++i)
                {
                    fprintf(log, "%02X", ptr[i]);
                    if ((i & 0x1F) == 0x1F) fprintf(log, "\n              ");
                }
                fprintf(log, "\n");
            }

            fflush(log);
            fclose(log);
        }
    }

    if (return_code == OS_SUCCESS)
    {
        /*
         * NOTE: It is likely that if the file system is mounted,
         * this call to stop the volume will fail.
         *
         * It would be prudent to first check the flags to ensure that
         * the filesystem is unmounted first, but this would break
         * compatibility with the existing unit tests.
         */
        return_code = OS_FileSysStopVolume_Impl(&token);

        /* Log the result of stopping the volume */
        {
            FILE *log = fopen("/tmp/logs/2305.txt", "a");
            if (log != NULL)
            {
                fprintf(log, "OS_rmfs: after OS_FileSysStopVolume_Impl() -> return_code=%d\n", (int)return_code);
                fflush(log);
                fclose(log);
            }
        }

        /* Free the entry in the master table  */
        return_code = OS_ObjectIdFinalizeDelete(return_code, &token);

        /* Log the finalization result */
        {
            FILE *log = fopen("/tmp/logs/2305.txt", "a");
            if (log != NULL)
            {
                fprintf(log, "OS_rmfs: after OS_ObjectIdFinalizeDelete() -> return_code=%d\n", (int)return_code);
                fprintf(log, "OS_rmfs: exiting with return_code=%d\n", (int)return_code);
                fflush(log);
                fclose(log);
            }
        }
    }
    else
    {
        return_code = OS_ERR_NAME_NOT_FOUND;

        /* Log name not found outcome */
        {
            FILE *log = fopen("/tmp/logs/2305.txt", "a");
            if (log != NULL)
            {
                fprintf(log, "OS_rmfs: name not found for devname=%p -> returning OS_ERR_NAME_NOT_FOUND (%d)\n", (const void *)devname, (int)return_code);
                fflush(log);
                fclose(log);
            }
        }
    }

    return return_code;
}
