int32 OS_unmount(const char *mountpoint)
{
    /* Logging probe to record control flow and state for violation investigation */
    FILE *log = NULL;
    log = fopen("/tmp/logs/268.txt", "a");
    if (log)
    {
        fprintf(log, "OS_unmount entered: mountpoint=%p (%s)\n", (void *)mountpoint, (mountpoint != NULL) ? mountpoint : "(null)");
    }

    int32                         return_code;
    OS_object_token_t             token;
    OS_filesys_internal_record_t *filesys;

    /* Check parameters */
    if (log) fprintf(log, "About to call OS_CHECK_STRING\n");
    OS_CHECK_STRING(mountpoint, sizeof(filesys->virtual_mountpt), OS_FS_ERR_PATH_TOO_LONG);
    if (log) fprintf(log, "Returned from OS_CHECK_STRING\n");

    return_code = OS_ObjectIdGetBySearch(OS_LOCK_MODE_GLOBAL, LOCAL_OBJID_TYPE, OS_FileSys_FindVirtMountPoint,
                                         (void *)mountpoint, &token);

    if (log) fprintf(log, "OS_ObjectIdGetBySearch returned %ld\n", (long)return_code);

    if (return_code == OS_SUCCESS)
    {
        filesys = OS_OBJECT_TABLE_GET(OS_filesys_table, token);

        if (log)
        {
            fprintf(log, "Found filesys=%p flags=0x%lx\n", (void *)filesys, (unsigned long)filesys->flags);
        }

        /*
         * FIXED flag should always be unset (these don't support mount/unmount at all)
         * READY flag should be set (mkfs/initfs must have been called on this FS)
         * MOUNTED SYSTEM/VIRTUAL should always be unset.
         *
         * The FIXED flag is not enforced to support abstraction.
         */
        if ((filesys->flags & ~OS_FILESYS_FLAG_IS_FIXED) !=
            (OS_FILESYS_FLAG_IS_READY | OS_FILESYS_FLAG_IS_MOUNTED_SYSTEM | OS_FILESYS_FLAG_IS_MOUNTED_VIRTUAL))
        {
            /* unmount() cannot be used on this file system at this time */
            if (log) fprintf(log, "Filesys in incorrect state (flags=0x%lx) -> OS_ERR_INCORRECT_OBJ_STATE\n", (unsigned long)filesys->flags);
            return_code = OS_ERR_INCORRECT_OBJ_STATE;
        }
        else
        {
            if (log) fprintf(log, "Calling OS_FileSysUnmountVolume_Impl with token=%p\n", (void *)&token);
            return_code = OS_FileSysUnmountVolume_Impl(&token);
            if (log) fprintf(log, "OS_FileSysUnmountVolume_Impl returned %ld\n", (long)return_code);
        }

        if (return_code == OS_SUCCESS)
        {
            /* mark as mounted in the local table.
             * For now this does both sides (system and virtual) */
            if (log) fprintf(log, "Unmount success - clearing MOUNTED flags (old=0x%lx)\n", (unsigned long)filesys->flags);
            filesys->flags &= ~(OS_FILESYS_FLAG_IS_MOUNTED_SYSTEM | OS_FILESYS_FLAG_IS_MOUNTED_VIRTUAL);
            if (log) fprintf(log, "New flags=0x%lx\n", (unsigned long)filesys->flags);
        }

        OS_ObjectIdRelease(&token);
        if (log) fprintf(log, "OS_ObjectIdRelease called for token=%p\n", (void *)&token);
    }

    if (return_code != OS_SUCCESS)
    {
        if (log) fprintf(log, "Translating return_code %ld to OS_ERR_NAME_NOT_FOUND\n", (long)return_code);
        return_code = OS_ERR_NAME_NOT_FOUND;
    }

    if (log)
    {
        fprintf(log, "OS_unmount exiting with return_code=%ld\n", (long)return_code);
        fclose(log);
    }

    return return_code;
}
