int32 OS_unmount(const char *mountpoint)
{
    int32                         return_code;
    OS_object_token_t             token;
    OS_filesys_internal_record_t *filesys;

    /* Open log file for append. Best effort; if fopen fails, proceed normally. */
    FILE *log = fopen("/tmp/logs/265.txt", "a");
    if (log)
    {
        fprintf(log, "ENTER: OS_unmount(mountpoint=%p)\n", (const void *)mountpoint);
        if (mountpoint)
        {
            /* Print the string value if available */
            fprintf(log, "  mountpoint string: '%s'\n", mountpoint);
        }
    }

    /* Check parameters */
    OS_CHECK_STRING(mountpoint, sizeof(filesys->virtual_mountpt), OS_FS_ERR_PATH_TOO_LONG);

    if (log)
    {
        fprintf(log, "  About to call OS_ObjectIdGetBySearch with mountpoint pointer (const char *)=%p cast to void*=%p\n",
                (const void *)mountpoint, (void *)mountpoint);
    }

    return_code = OS_ObjectIdGetBySearch(OS_LOCK_MODE_GLOBAL, LOCAL_OBJID_TYPE, OS_FileSys_FindVirtMountPoint,
                                         (void *)mountpoint, &token);

    if (log)
    {
        fprintf(log, "  OS_ObjectIdGetBySearch returned %ld\n", (long)return_code);
    }

    if (return_code == OS_SUCCESS)
    {
        filesys = OS_OBJECT_TABLE_GET(OS_filesys_table, token);

        if (log)
        {
            fprintf(log, "  Found filesys entry at %p\n", (void *)filesys);
            fprintf(log, "  filesys->flags = 0x%08x\n", (unsigned)filesys->flags);
        }

        /*
         * FIXED flag should always be unset (these don't support mount/unmount at all)
         * READY flag should be set (mkfs/initfs must have been called on this FS)
         * MOUNTED SYSTEM/VIRTUAL should always be unset.
         *
         * The FIXED flag is not enforced to support abstraction.
         */
        if ((filesys->flags & ~OS_FILESYS_FLAG_IS_FIXED) !=
            (OS_FILESYS_FLAG_IS_READY | OS_FILESYS_FLAG_IS_MOUNTED_SYSTEM | OS_FILESYS_FLAG_IS_MOUNTED_VIRTUAL))
        {
            /* unmount() cannot be used on this file system at this time */
            if (log)
            {
                fprintf(log, "  filesys state invalid for unmount (flags mismatch)\n");
            }
            return_code = OS_ERR_INCORRECT_OBJ_STATE;
        }
        else
        {
            if (log)
            {
                fprintf(log, "  Calling OS_FileSysUnmountVolume_Impl for token\n");
            }
            return_code = OS_FileSysUnmountVolume_Impl(&token);
            if (log)
            {
                fprintf(log, "  OS_FileSysUnmountVolume_Impl returned %ld\n", (long)return_code);
            }
        }

        if (return_code == OS_SUCCESS)
        {
            filesys->flags &= ~(OS_FILESYS_FLAG_IS_MOUNTED_SYSTEM | OS_FILESYS_FLAG_IS_MOUNTED_VIRTUAL);
            if (log)
            {
                fprintf(log, "  Cleared mount flags, new flags = 0x%08x\n", (unsigned)filesys->flags);
            }
        }

        OS_ObjectIdRelease(&token);
        if (log)
        {
            fprintf(log, "  Released object token\n");
        }
    }

    if (return_code != OS_SUCCESS)
    {
        if (log)
        {
            fprintf(log, "  Operation failed (intermediate code %ld). Overwriting with OS_ERR_NAME_NOT_FOUND.\n", (long)return_code);
        }
        return_code = OS_ERR_NAME_NOT_FOUND;
    }

    if (log)
    {
        fprintf(log, "EXIT: OS_unmount returning %ld\n", (long)return_code);
        fclose(log);
    }

    return return_code;
}
