int32 OS_unmount(const char *mountpoint)
{
    int32                         return_code;
    OS_object_token_t             token;
    OS_filesys_internal_record_t *filesys;
    FILE *os_unmount_log_fp = NULL;

    /* Try to open log file for appending. If this fails, proceed without logging. */
    os_unmount_log_fp = fopen("/tmp/logs/265.txt", "a");
    if (os_unmount_log_fp != NULL)
    {
        fprintf(os_unmount_log_fp, "OS_unmount: ENTER, mountpoint_ptr=%p", (void *)mountpoint);
        if (mountpoint != NULL)
        {
            /* print up to a reasonable length to avoid overly long logs */
            fprintf(os_unmount_log_fp, " mountpoint=\"%s\"", mountpoint);
        }
        fprintf(os_unmount_log_fp, "\n");
        fflush(os_unmount_log_fp);
    }

    /* Check parameters */
    OS_CHECK_STRING(mountpoint, sizeof(filesys->virtual_mountpt), OS_FS_ERR_PATH_TOO_LONG);

    if (os_unmount_log_fp != NULL)
    {
        fprintf(os_unmount_log_fp, "OS_unmount: After OS_CHECK_STRING, about to call OS_ObjectIdGetBySearch with arg=(void*)mountpoint (const->non-const cast)\n");
        fflush(os_unmount_log_fp);
    }

    return_code = OS_ObjectIdGetBySearch(OS_LOCK_MODE_GLOBAL, LOCAL_OBJID_TYPE, OS_FileSys_FindVirtMountPoint,
                                         (void *)mountpoint, &token);

    if (os_unmount_log_fp != NULL)
    {
        fprintf(os_unmount_log_fp, "OS_unmount: OS_ObjectIdGetBySearch returned %d\n", (int)return_code);
        fflush(os_unmount_log_fp);
    }

    if (return_code == OS_SUCCESS)
    {
        filesys = OS_OBJECT_TABLE_GET(OS_filesys_table, token);

        if (os_unmount_log_fp != NULL)
        {
            /* Log the filesys pointer and flags value to capture state */
            fprintf(os_unmount_log_fp, "OS_unmount: Found filesys ptr=%p", (void *)filesys);
            /* filesys may be NULL in some error cases; guard access */
            if (filesys != NULL)
            {
                fprintf(os_unmount_log_fp, " flags=0x%08x", (unsigned int)filesys->flags);
            }
            fprintf(os_unmount_log_fp, "\n");
            fflush(os_unmount_log_fp);
        }

        /*
         * FIXED flag should always be unset (these don't support mount/unmount at all)
         * READY flag should be set (mkfs/initfs must have been called on this FS)
         * MOUNTED SYSTEM/VIRTUAL should always be unset.
         *
         * The FIXED flag is not enforced to support abstraction.
         */
        if ((filesys->flags & ~OS_FILESYS_FLAG_IS_FIXED) !=
            (OS_FILESYS_FLAG_IS_READY | OS_FILESYS_FLAG_IS_MOUNTED_SYSTEM | OS_FILESYS_FLAG_IS_MOUNTED_VIRTUAL))
        {
            /* unmount() cannot be used on this file system at this time */
            if (os_unmount_log_fp != NULL)
            {
                fprintf(os_unmount_log_fp, "OS_unmount: filesys in incorrect state, setting return_code=OS_ERR_INCORRECT_OBJ_STATE\n");
                fflush(os_unmount_log_fp);
            }

            return_code = OS_ERR_INCORRECT_OBJ_STATE;
        }
        else
        {
            if (os_unmount_log_fp != NULL)
            {
                fprintf(os_unmount_log_fp, "OS_unmount: filesys in correct state, calling OS_FileSysUnmountVolume_Impl\n");
                fflush(os_unmount_log_fp);
            }

            return_code = OS_FileSysUnmountVolume_Impl(&token);

            if (os_unmount_log_fp != NULL)
            {
                fprintf(os_unmount_log_fp, "OS_unmount: OS_FileSysUnmountVolume_Impl returned %d\n", (int)return_code);
                fflush(os_unmount_log_fp);
            }
        }

        if (return_code == OS_SUCCESS)
        {
            /* mark as mounted in the local table.
             * For now this does both sides (system and virtual) */
            if (os_unmount_log_fp != NULL)
            {
                fprintf(os_unmount_log_fp, "OS_unmount: Clearing MOUNTED flags on filesys\n");
                fflush(os_unmount_log_fp);
            }
            filesys->flags &= ~(OS_FILESYS_FLAG_IS_MOUNTED_SYSTEM | OS_FILESYS_FLAG_IS_MOUNTED_VIRTUAL);
        }

        if (os_unmount_log_fp != NULL)
        {
            fprintf(os_unmount_log_fp, "OS_unmount: Releasing token and exiting success/cleanup branch\n");
            fflush(os_unmount_log_fp);
        }

        OS_ObjectIdRelease(&token);
    }

    if (return_code != OS_SUCCESS)
    {
        if (os_unmount_log_fp != NULL)
        {
            fprintf(os_unmount_log_fp, "OS_unmount: About to map return_code=%d to OS_ERR_NAME_NOT_FOUND and return\n", (int)return_code);
            fflush(os_unmount_log_fp);
        }

        return_code = OS_ERR_NAME_NOT_FOUND;
    }
    else
    {
        if (os_unmount_log_fp != NULL)
        {
            fprintf(os_unmount_log_fp, "OS_unmount: Exiting with success return_code=%d\n", (int)return_code);
            fflush(os_unmount_log_fp);
        }
    }

    if (os_unmount_log_fp != NULL)
    {
        fclose(os_unmount_log_fp);
    }

    return return_code;
}
