int32 OS_unmount(const char *mountpoint)
{
    int32                         return_code;
    OS_object_token_t             token;
    OS_filesys_internal_record_t *filesys;

    /* Minimal safe entry log: do not dereference mountpoint before validation */
    {
        FILE *log = fopen("/tmp/logs/265.txt", "a");
        if (log)
        {
            fprintf(log, "OS_unmount ENTRY: mountpoint_ptr=%p\n", (void *)mountpoint);
            fclose(log);
        }
    }

    /* Check parameters */
    OS_CHECK_STRING(mountpoint, sizeof(filesys->virtual_mountpt), OS_FS_ERR_PATH_TOO_LONG);

    /* After validation, log the mountpoint string (safe to dereference now) */
    {
        FILE *log = fopen("/tmp/logs/265.txt", "a");
        if (log)
        {
            fprintf(log, "OS_unmount: mountpoint validated: '%s'\n", mountpoint ? mountpoint : "(null)");
            fclose(log);
        }
    }

    /* Log that we're about to call the search by casting away const (point of interest) */
    {
        FILE *log = fopen("/tmp/logs/265.txt", "a");
        if (log)
        {
            fprintf(log, "OS_unmount: calling OS_ObjectIdGetBySearch with arg=(void *)mountpoint (casting away const) mountpoint_ptr=%p name='%s'\n",
                    (void *)mountpoint, mountpoint ? mountpoint : "(null)");
            fclose(log);
        }
    }

    return_code = OS_ObjectIdGetBySearch(OS_LOCK_MODE_GLOBAL, LOCAL_OBJID_TYPE, OS_FileSys_FindVirtMountPoint,
                                         (void *)mountpoint, &token);

    {
        FILE *log = fopen("/tmp/logs/265.txt", "a");
        if (log)
        {
            fprintf(log, "OS_unmount: OS_ObjectIdGetBySearch returned %ld\n", (long)return_code);
            fclose(log);
        }
    }

    if (return_code == OS_SUCCESS)
    {
        filesys = OS_OBJECT_TABLE_GET(OS_filesys_table, token);

        {
            FILE *log = fopen("/tmp/logs/265.txt", "a");
            if (log)
            {
                fprintf(log, "OS_unmount: found filesys record at %p flags=0x%08x\n",
                        (void *)filesys, (unsigned int)filesys->flags);
                fclose(log);
            }
        }

        /*
         * FIXED flag should always be unset (these don't support mount/unmount at all)
         * READY flag should be set (mkfs/initfs must have been called on this FS)
         * MOUNTED SYSTEM/VIRTUAL should always be unset.
         *
         * The FIXED flag is not enforced to support abstraction.
         */
        if ((filesys->flags & ~OS_FILESYS_FLAG_IS_FIXED) !=
            (OS_FILESYS_FLAG_IS_READY | OS_FILESYS_FLAG_IS_MOUNTED_SYSTEM | OS_FILESYS_FLAG_IS_MOUNTED_VIRTUAL))
        {
            /* unmount() cannot be used on this file system at this time */
            {
                FILE *log = fopen("/tmp/logs/265.txt", "a");
                if (log)
                {
                    fprintf(log, "OS_unmount: incorrect object state: flags=0x%08x\n", (unsigned int)filesys->flags);
                    fclose(log);
                }
            }

            return_code = OS_ERR_INCORRECT_OBJ_STATE;
        }
        else
        {
            {
                FILE *log = fopen("/tmp/logs/265.txt", "a");
                if (log)
                {
                    fprintf(log, "OS_unmount: calling OS_FileSysUnmountVolume_Impl with token\n");
                    fclose(log);
                }
            }

            return_code = OS_FileSysUnmountVolume_Impl(&token);

            {
                FILE *log = fopen("/tmp/logs/265.txt", "a");
                if (log)
                {
                    fprintf(log, "OS_unmount: OS_FileSysUnmountVolume_Impl returned %ld\n", (long)return_code);
                    fclose(log);
                }
            }
        }

        if (return_code == OS_SUCCESS)
        {
            filesys->flags &= ~(OS_FILESYS_FLAG_IS_MOUNTED_SYSTEM | OS_FILESYS_FLAG_IS_MOUNTED_VIRTUAL);

            {
                FILE *log = fopen("/tmp/logs/265.txt", "a");
                if (log)
                {
                    fprintf(log, "OS_unmount: cleared mount flags; new flags=0x%08x\n", (unsigned int)filesys->flags);
                    fclose(log);
                }
            }
        }

        OS_ObjectIdRelease(&token);

        {
            FILE *log = fopen("/tmp/logs/265.txt", "a");
            if (log)
            {
                fprintf(log, "OS_unmount: released token\n");
                fclose(log);
            }
        }
    }

    if (return_code != OS_SUCCESS)
    {
        /* Preserve original behavior: map failures to NAME_NOT_FOUND */
        {
            FILE *log = fopen("/tmp/logs/265.txt", "a");
            if (log)
            {
                fprintf(log, "OS_unmount: mapping return_code %ld to OS_ERR_NAME_NOT_FOUND\n", (long)return_code);
                fclose(log);
            }
        }

        return_code = OS_ERR_NAME_NOT_FOUND;
    }

    {
        FILE *log = fopen("/tmp/logs/265.txt", "a");
        if (log)
        {
            fprintf(log, "OS_unmount EXIT: return_code=%ld\n", (long)return_code);
            fclose(log);
        }
    }

    return return_code;
}
