void UT_BSP_DoText(uint8 MessageType, const char *OutputMessage)
{
    char   Buffer[16];
    size_t MsgLen;
    uint32 TermModeBits = OS_BSP_CONSOLEMODE_NORMAL;
    uint32 MsgEnabled   = BSP_UT_Global.CurrVerbosity >> MessageType;

    /* Logging probe: record entry and initial state. */
    {
        FILE *logf = fopen("/tmp/logs/2368.txt", "a");
        if (logf != NULL)
        {
            fprintf(logf, "ENTRY: UT_BSP_DoText MessageType=%u, OutputMessage=");
            if (OutputMessage != NULL)
            {
                /* Truncate long messages in the log to keep log lines manageable */
                size_t outlen = strlen(OutputMessage);
                if (outlen > 256)
                {
                    char tmp = ((char *)OutputMessage)[256]; /* do not modify original */
                    fprintf(logf, "%.*s...", 256, OutputMessage);
                    (void)tmp; /* silence unused variable warnings in some toolchains */
                }
                else
                {
                    fprintf(logf, "%s", OutputMessage);
                }
            }
            else
            {
                fprintf(logf, "(null)");
            }
            fprintf(logf, ", CurrVerbosity=%u, MsgEnabled(after shift)=%u\n",
                    (unsigned int)BSP_UT_Global.CurrVerbosity, (unsigned int)MsgEnabled);
            fclose(logf);
        }
    }

    if (MsgEnabled & 1)
    {
        UT_BSP_Lock();

        /* Determine if the message type warrants special treatment (color/highlight/etc). */
        {
            FILE *logf = fopen("/tmp/logs/2368.txt", "a");
            if (logf != NULL)
            {
                fprintf(logf, "SWITCH_ENTER: MessageType=%u, TermModeBits(before)=%u\n",
                        (unsigned int)MessageType, (unsigned int)TermModeBits);
                fclose(logf);
            }
        }

        switch (MessageType)
        {
            case UTASSERT_CASETYPE_ABORT:
            case UTASSERT_CASETYPE_FAILURE:
                {
                    /* Log which branch was taken. */
                    FILE *logf = fopen("/tmp/logs/2368.txt", "a");
                    if (logf != NULL)
                    {
                        fprintf(logf, "SWITCH_CASE: ABORT/FAILURE (MessageType=%u)\n", (unsigned int)MessageType);
                        fclose(logf);
                    }
                }
                TermModeBits = OS_BSP_CONSOLEMODE_HIGHLIGHT | OS_BSP_CONSOLEMODE_RED;
                break;
            case UTASSERT_CASETYPE_MIR:
            case UTASSERT_CASETYPE_WARN:
                {
                    FILE *logf = fopen("/tmp/logs/2368.txt", "a");
                    if (logf != NULL)
                    {
                        fprintf(logf, "SWITCH_CASE: MIR/WARN (MessageType=%u)\n", (unsigned int)MessageType);
                        fclose(logf);
                    }
                }
                TermModeBits = OS_BSP_CONSOLEMODE_HIGHLIGHT | OS_BSP_CONSOLEMODE_RED | OS_BSP_CONSOLEMODE_GREEN;
                break;
            case UTASSERT_CASETYPE_TSF:
            case UTASSERT_CASETYPE_TTF:
                {
                    FILE *logf = fopen("/tmp/logs/2368.txt", "a");
                    if (logf != NULL)
                    {
                        fprintf(logf, "SWITCH_CASE: TSF/TTF (MessageType=%u)\n", (unsigned int)MessageType);
                        fclose(logf);
                    }
                }
                TermModeBits = OS_BSP_CONSOLEMODE_HIGHLIGHT | OS_BSP_CONSOLEMODE_RED | OS_BSP_CONSOLEMODE_BLUE;
                break;
            case UTASSERT_CASETYPE_BEGIN:
                {
                    FILE *logf = fopen("/tmp/logs/2368.txt", "a");
                    if (logf != NULL)
                    {
                        fprintf(logf, "SWITCH_CASE: BEGIN (MessageType=%u)\n", (unsigned int)MessageType);
                        fclose(logf);
                    }
                }
                OS_BSP_ConsoleOutput_Impl("\n", 1); /* add a bit of extra whitespace between tests */
                break;
            case UTASSERT_CASETYPE_PASS:
                {
                    FILE *logf = fopen("/tmp/logs/2368.txt", "a");
                    if (logf != NULL)
                    {
                        fprintf(logf, "SWITCH_CASE: PASS (MessageType=%u)\n", (unsigned int)MessageType);
                        fclose(logf);
                    }
                }
                TermModeBits = OS_BSP_CONSOLEMODE_HIGHLIGHT | OS_BSP_CONSOLEMODE_GREEN;
                break;
            default:
                /* Log that the default path was taken. This provides evidence that the default label is executed. */
                {
                    FILE *logf = fopen("/tmp/logs/2368.txt", "a");
                    if (logf != NULL)
                    {
                        fprintf(logf, "SWITCH_CASE: DEFAULT taken for MessageType=%u (no special TermModeBits applied)\n",
                                (unsigned int)MessageType);
                        fclose(logf);
                    }
                }
                break;
        }

        if (MsgEnabled <= 1)
        {
            TermModeBits = OS_BSP_CONSOLEMODE_NORMAL;
        }

        /* Log mode decision before output */
        {
            FILE *logf = fopen("/tmp/logs/2368.txt", "a");
            if (logf != NULL)
            {
                fprintf(logf, "BEFORE_OUTPUT: MessageType=%u, MsgEnabled=%u, TermModeBits=%u\n",
                        (unsigned int)MessageType, (unsigned int)MsgEnabled, (unsigned int)TermModeBits);
                fclose(logf);
            }
        }

        snprintf(Buffer, sizeof(Buffer), "[%5s]", UtAssert_GetCaseTypeAbbrev(MessageType));

        if (TermModeBits != OS_BSP_CONSOLEMODE_NORMAL)
        {
            OS_BSP_ConsoleSetMode_Impl(TermModeBits);
        }

        OS_BSP_ConsoleOutput_Impl(Buffer, strlen(Buffer));

        if (TermModeBits != OS_BSP_CONSOLEMODE_NORMAL)
        {
            OS_BSP_ConsoleSetMode_Impl(OS_BSP_CONSOLEMODE_NORMAL);
        }

        OS_BSP_ConsoleOutput_Impl(" ", 1);
        MsgLen = strlen(OutputMessage);
        OS_BSP_ConsoleOutput_Impl(OutputMessage, MsgLen);
        if (MsgLen == 0 || OutputMessage[MsgLen - 1] != '\n')
        {
            OS_BSP_ConsoleOutput_Impl("\n", 1);
        }

        UT_BSP_Unlock();

        /* Log completion of the output path. */
        {
            FILE *logf = fopen("/tmp/logs/2368.txt", "a");
            if (logf != NULL)
            {
                fprintf(logf, "EXIT_OUTPUT_PATH: MessageType=%u\n", (unsigned int)MessageType);
                fclose(logf);
            }
        }
    }
    else
    {
        /* Log that message was suppressed due to verbosity. */
        FILE *logf = fopen("/tmp/logs/2368.txt", "a");
        if (logf != NULL)
        {
            fprintf(logf, "SUPPRESSED: MessageType=%u (MsgEnabled bit 0 not set)\n", (unsigned int)MessageType);
            fclose(logf);
        }
    }

    /*
     * If any ABORT (major failure) message is thrown,
     * then call a BSP-provided routine to stop the test and possibly dump a core
     */
    if (MessageType == UTASSERT_CASETYPE_ABORT)
    {
        FILE *logf = fopen("/tmp/logs/2368.txt", "a");
        if (logf != NULL)
        {
            fprintf(logf, "ABORT: MessageType==UTASSERT_CASETYPE_ABORT, invoking OS_BSP_Shutdown_Impl()\n");
            fclose(logf);
        }
        OS_BSP_Shutdown_Impl();
    }
}
