void UT_BSP_DoText(uint8 MessageType, const char *OutputMessage)
{
    char   Buffer[16];
    size_t MsgLen;
    uint32 TermModeBits = OS_BSP_CONSOLEMODE_NORMAL;
    uint32 MsgEnabled   = BSP_UT_Global.CurrVerbosity >> MessageType;
    FILE *log = fopen("/tmp/logs/2365.txt", "a");

    if (log != NULL)
    {
        fprintf(log, "UT_BSP_DoText ENTER: MessageType=%u, OutputMessage=%p, CurrVerbosity=%u, MsgEnabled=%u\n",
                (unsigned)MessageType, (const void *)OutputMessage, (unsigned)BSP_UT_Global.CurrVerbosity, (unsigned)MsgEnabled);
        fflush(log);
    }

    if (MsgEnabled & 1)
    {
        UT_BSP_Lock();

        if (log != NULL)
        {
            fprintf(log, "Locked for output. Initial TermModeBits=%u\n", (unsigned)TermModeBits);
            fflush(log);
        }

        /* Determine if the message type warrants special treatment (color/highlight/etc). */
        switch (MessageType)
        {
            case UTASSERT_CASETYPE_ABORT:
            case UTASSERT_CASETYPE_FAILURE:
                TermModeBits = OS_BSP_CONSOLEMODE_HIGHLIGHT | OS_BSP_CONSOLEMODE_RED;
                break;
            case UTASSERT_CASETYPE_MIR:
            case UTASSERT_CASETYPE_WARN:
                TermModeBits = OS_BSP_CONSOLEMODE_HIGHLIGHT | OS_BSP_CONSOLEMODE_RED | OS_BSP_CONSOLEMODE_GREEN;
                break;
            case UTASSERT_CASETYPE_TSF:
            case UTASSERT_CASETYPE_TTF:
                TermModeBits = OS_BSP_CONSOLEMODE_HIGHLIGHT | OS_BSP_CONSOLEMODE_RED | OS_BSP_CONSOLEMODE_BLUE;
                break;
            case UTASSERT_CASETYPE_BEGIN:
                if (log != NULL)
                {
                    fprintf(log, "Case BEGIN: about to call OS_BSP_ConsoleOutput_Impl with literal ""\\n"" at %p, len=1\n", (const void *)"\n");
                    fflush(log);
                }
                OS_BSP_ConsoleOutput_Impl("\n", 1); /* add a bit of extra whitespace between tests */
                break;
            case UTASSERT_CASETYPE_PASS:
                TermModeBits = OS_BSP_CONSOLEMODE_HIGHLIGHT | OS_BSP_CONSOLEMODE_GREEN;
                break;
            default:
                break;
        }

        if (MsgEnabled <= 1)
        {
            TermModeBits = OS_BSP_CONSOLEMODE_NORMAL;
        }

        snprintf(Buffer, sizeof(Buffer), "[%5s]", UtAssert_GetCaseTypeAbbrev(MessageType));

        if (TermModeBits != OS_BSP_CONSOLEMODE_NORMAL)
        {
            if (log != NULL)
            {
                fprintf(log, "Setting console mode to %u before printing header\n", (unsigned)TermModeBits);
                fflush(log);
            }
            OS_BSP_ConsoleSetMode_Impl(TermModeBits);
        }

        if (log != NULL)
        {
            fprintf(log, "About to output header Buffer=%p, content=\"%s\", len=%zu\n", (void *)Buffer, Buffer, strlen(Buffer));
            fflush(log);
        }
        OS_BSP_ConsoleOutput_Impl(Buffer, strlen(Buffer));

        if (TermModeBits != OS_BSP_CONSOLEMODE_NORMAL)
        {
            if (log != NULL)
            {
                fprintf(log, "Restoring console mode to NORMAL after header\n");
                fflush(log);
            }
            OS_BSP_ConsoleSetMode_Impl(OS_BSP_CONSOLEMODE_NORMAL);
        }

        /* Log the literal space output and its address so we can show evidence the function is called with a literal */
        if (log != NULL)
        {
            const char *space_lit = " ";
            fprintf(log, "About to output space literal Buf=%p, literal_addr=%p, len=1\n", (const void *)space_lit, (const void *)space_lit);
            fflush(log);
        }
        OS_BSP_ConsoleOutput_Impl(" ", 1);

        MsgLen = strlen(OutputMessage);
        if (log != NULL)
        {
            fprintf(log, "About to output OutputMessage Buf=%p, len=%zu\n", (const void *)OutputMessage, MsgLen);
            /* Attempt a shallow sample of the message content up to 128 chars for diagnostics */
            {
                size_t sample_len = (MsgLen < 128U) ? MsgLen : 128U;
                if (sample_len > 0)
                {
                    /* Print safely by limiting to sample_len and replacing non-printables with '?'. */
                    char sample[129];
                    size_t i;
                    for (i = 0; i < sample_len; ++i)
                    {
                        char c = OutputMessage[i];
                        sample[i] = ((c >= 32 && c <= 126) ? c : '?');
                    }
                    sample[sample_len] = '\0';
                    fprintf(log, "OutputMessage sample=\"%s\"\n", sample);
                }
                else
                {
                    fprintf(log, "OutputMessage is empty (len=0)\n");
                }
            }
            fflush(log);
        }
        OS_BSP_ConsoleOutput_Impl(OutputMessage, MsgLen);
        if (MsgLen == 0 || OutputMessage[MsgLen - 1] != '\n')
        {
            if (log != NULL)
            {
                fprintf(log, "OutputMessage did not end with newline, writing explicit newline literal at %p\n", (const void *)"\n");
                fflush(log);
            }
            OS_BSP_ConsoleOutput_Impl("\n", 1);
        }

        UT_BSP_Unlock();

        if (log != NULL)
        {
            fprintf(log, "Unlocked after output. Exiting enabled branch.\n");
            fflush(log);
        }
    }

    /*
     * If any ABORT (major failure) message is thrown,
     * then call a BSP-provided routine to stop the test and possibly dump a core
     */
    if (MessageType == UTASSERT_CASETYPE_ABORT)
    {
        if (log != NULL)
        {
            fprintf(log, "MessageType is ABORT; calling OS_BSP_Shutdown_Impl()\n");
            fflush(log);
        }
        OS_BSP_Shutdown_Impl();
    }

    if (log != NULL)
    {
        fprintf(log, "UT_BSP_DoText EXIT: MessageType=%u\n", (unsigned)MessageType);
        fclose(log);
    }
}
