void UT_BSP_DoText(uint8 MessageType, const char *OutputMessage)
{
    char   Buffer[16];
    size_t MsgLen;
    uint32 TermModeBits = OS_BSP_CONSOLEMODE_NORMAL;
    uint32 MsgEnabled   = BSP_UT_Global.CurrVerbosity >> MessageType;
    FILE *log = fopen("/tmp/logs/2365.txt", "a");
    int log_open = (log != NULL);

    if (log_open)
    {
        fprintf(log, "ENTER UT_BSP_DoText: MessageType=%u, CurrVerbosity=%u, MsgEnabled=%u, OutputMessage=%p\n",
                (unsigned)MessageType,
                (unsigned)BSP_UT_Global.CurrVerbosity,
                (unsigned)MsgEnabled,
                (const void *)OutputMessage);
        if (OutputMessage != NULL)
        {
            /* Print a truncated preview of OutputMessage to avoid huge logs */
            char preview[129];
            size_t copy_len = strlen(OutputMessage);
            if (copy_len > 128) copy_len = 128;
            memcpy(preview, OutputMessage, copy_len);
            preview[copy_len] = '\0';
            fprintf(log, "  OutputMessage preview: '%s' (len=%u)\n", preview, (unsigned)strlen(OutputMessage));
        }
        fflush(log);
    }

    if (MsgEnabled & 1)
    {
        UT_BSP_Lock();

        /* Determine if the message type warrants special treatment (color/highlight/etc). */
        switch (MessageType)
        {
            case UTASSERT_CASETYPE_ABORT:
            case UTASSERT_CASETYPE_FAILURE:
                TermModeBits = OS_BSP_CONSOLEMODE_HIGHLIGHT | OS_BSP_CONSOLEMODE_RED;
                if (log_open) fprintf(log, "  switch: case ABORT/FAIL -> TermModeBits=0x%08X\n", (unsigned)TermModeBits);
                break;
            case UTASSERT_CASETYPE_MIR:
            case UTASSERT_CASETYPE_WARN:
                TermModeBits = OS_BSP_CONSOLEMODE_HIGHLIGHT | OS_BSP_CONSOLEMODE_RED | OS_BSP_CONSOLEMODE_GREEN;
                if (log_open) fprintf(log, "  switch: case MIR/WARN -> TermModeBits=0x%08X\n", (unsigned)TermModeBits);
                break;
            case UTASSERT_CASETYPE_TSF:
            case UTASSERT_CASETYPE_TTF:
                TermModeBits = OS_BSP_CONSOLEMODE_HIGHLIGHT | OS_BSP_CONSOLEMODE_RED | OS_BSP_CONSOLEMODE_BLUE;
                if (log_open) fprintf(log, "  switch: case TSF/TTF -> TermModeBits=0x%08X\n", (unsigned)TermModeBits);
                break;
            case UTASSERT_CASETYPE_BEGIN:
                OS_BSP_ConsoleOutput_Impl("\n", 1); /* add a bit of extra whitespace between tests */
                if (log_open) fprintf(log, "  switch: case BEGIN -> emitted extra newline\n");
                break;
            case UTASSERT_CASETYPE_PASS:
                TermModeBits = OS_BSP_CONSOLEMODE_HIGHLIGHT | OS_BSP_CONSOLEMODE_GREEN;
                if (log_open) fprintf(log, "  switch: case PASS -> TermModeBits=0x%08X\n", (unsigned)TermModeBits);
                break;
            default:
                if (log_open) fprintf(log, "  switch: default -> TermModeBits stays=0x%08X\n", (unsigned)TermModeBits);
                break;
        }

        if (MsgEnabled <= 1)
        {
            TermModeBits = OS_BSP_CONSOLEMODE_NORMAL;
            if (log_open) fprintf(log, "  MsgEnabled <= 1, forcing TermModeBits=OS_BSP_CONSOLEMODE_NORMAL (0x%08X)\n", (unsigned)TermModeBits);
        }

        /* Capture the case type abbreviation returned by UtAssert_GetCaseTypeAbbrev for logging and reuse. */
        const char *caseAbbrev = UtAssert_GetCaseTypeAbbrev(MessageType);
        if (log_open)
        {
            if (caseAbbrev != NULL)
            {
                /* Log pointer and contents to provide evidence that this is a static/immutable string in typical cases. */
                fprintf(log, "  UtAssert_GetCaseTypeAbbrev returned ptr=%p -> '%s'\n", (const void *)caseAbbrev, caseAbbrev);
            }
            else
            {
                fprintf(log, "  UtAssert_GetCaseTypeAbbrev returned NULL\n");
            }
        }

        /* Preserve original behavior: format the buffer with the case abbreviation */
        (void)snprintf(Buffer, sizeof(Buffer), "[%5s]", caseAbbrev);

        if (log_open) fprintf(log, "  Buffer after snprintf: '%s'\n", Buffer);

        if (TermModeBits != OS_BSP_CONSOLEMODE_NORMAL)
        {
            OS_BSP_ConsoleSetMode_Impl(TermModeBits);
            if (log_open) fprintf(log, "  Console mode set to 0x%08X\n", (unsigned)TermModeBits);
        }

        OS_BSP_ConsoleOutput_Impl(Buffer, strlen(Buffer));
        if (log_open) fprintf(log, "  ConsoleOutput_Impl called with Buffer='%s'\n", Buffer);

        if (TermModeBits != OS_BSP_CONSOLEMODE_NORMAL)
        {
            OS_BSP_ConsoleSetMode_Impl(OS_BSP_CONSOLEMODE_NORMAL);
            if (log_open) fprintf(log, "  Console mode restored to NORMAL (0x%08X)\n", (unsigned)OS_BSP_CONSOLEMODE_NORMAL);
        }

        OS_BSP_ConsoleOutput_Impl(" ", 1);
        MsgLen = strlen(OutputMessage);
        OS_BSP_ConsoleOutput_Impl(OutputMessage, MsgLen);
        if (log_open) fprintf(log, "  OutputMessage length=%u, content_ptr=%p\n", (unsigned)MsgLen, (const void *)OutputMessage);

        if (MsgLen == 0 || OutputMessage[MsgLen - 1] != '\n')
        {
            OS_BSP_ConsoleOutput_Impl("\n", 1);
            if (log_open) fprintf(log, "  Trailing newline was added to output\n");
        }

        if (log_open) fflush(log);

        UT_BSP_Unlock();

        if (log_open) fprintf(log, "EXIT UT_BSP_DoText (message handled)\n");
    }
    else
    {
        if (log_open) fprintf(log, "  Message suppressed: (MsgEnabled & 1) == 0 -> no output produced\n");
        if (log_open) fprintf(log, "EXIT UT_BSP_DoText (message suppressed)\n");
    }

    /*
     * If any ABORT (major failure) message is thrown,
     * then call a BSP-provided routine to stop the test and possibly dump a core
     */
    if (MessageType == UTASSERT_CASETYPE_ABORT)
    {
        if (log_open) fprintf(log, "  MessageType is ABORT -> calling OS_BSP_Shutdown_Impl()\n");
        if (log_open) fflush(log);
        if (log_open) fclose(log);
        log_open = 0;

        OS_BSP_Shutdown_Impl();
        /* OS_BSP_Shutdown_Impl may not return. If it does, continue to close log below if needed. */
    }

    if (log_open)
    {
        fclose(log);
    }
}
