void UT_BSP_Setup(void)
{
    uint8        UserShift;
    uint32       ArgC;
    char *const *ArgV;
    FILE *log = fopen("/tmp/logs/2953.txt", "a");

    if (log)
    {
        fprintf(log, "UT_BSP_Setup: entered\n");
    }

    memset(&BSP_UT_Global, 0, sizeof(BSP_UT_Global));

    if (log)
    {
        fprintf(log, "UT_BSP_Setup: BSP_UT_Global zeroed\n");
    }

    UserShift = UTASSERT_CASETYPE_NONE;
    ArgC      = OS_BSP_GetArgC();

    if (log)
    {
        fprintf(log, "UT_BSP_Setup: OS_BSP_GetArgC returned ArgC=%lu\n", (unsigned long)ArgC);
    }

    if (ArgC > 0)
    {
        ArgV = OS_BSP_GetArgV();

        if (log)
        {
            void *argv_ptr = (void *)ArgV;
            void *argv0_ptr = NULL;

            /* Reading ArgV[0] here mirrors the original code's access (it may crash if ArgV is invalid) */
            if (ArgV != NULL)
            {
                argv0_ptr = (void *)ArgV[0];
            }

            fprintf(log, "UT_BSP_Setup: initial ArgV=%p ArgV[0]=%p\n", argv_ptr, argv0_ptr);
        }

        while (ArgC > 0)
        {
            --ArgC;

            if (log)
            {
                void *argv_ptr = (void *)ArgV;
                void *argv0_ptr = NULL;
                if (ArgV != NULL)
                {
                    argv0_ptr = (void *)ArgV[0];
                }
                fprintf(log, "UT_BSP_Setup: loop start remaining ArgC=%lu ArgV=%p ArgV[0]=%p\n",
                        (unsigned long)ArgC, argv_ptr, argv0_ptr);
            }

            /* The comparisons below intentionally remain identical to the original code to preserve behavior. */
            if (strcmp(ArgV[0], "-d") == 0)
            {
                if (log)
                {
                    fprintf(log, "UT_BSP_Setup: strcmp(ArgV[0], \"-d\") == 0 (ArgV[0]=%p)\n", (void *)ArgV[0]);
                }
                UserShift = UTASSERT_CASETYPE_DEBUG;
            }

            if (strcmp(ArgV[0], "-q") == 0)
            {
                if (log)
                {
                    fprintf(log, "UT_BSP_Setup: strcmp(ArgV[0], \"-q\") == 0 (ArgV[0]=%p)\n", (void *)ArgV[0]);
                }
                UserShift = UTASSERT_CASETYPE_FAILURE;
            }

            /* Note: This keeps the original evaluation order: strcmp(...) is evaluated before ArgC > 0. */
            if (strcmp(ArgV[0], "-v") == 0 && ArgC > 0)
            {
                --ArgC;

                if (log)
                {
                    void *argv1_ptr = NULL;
                    if (ArgV != NULL)
                    {
                        argv1_ptr = (void *)ArgV[1];
                    }
                    fprintf(log, "UT_BSP_Setup: matched -v ArgV[0]=%p ArgV[1]=%p\n", (void *)ArgV[0], argv1_ptr);
                }

                UserShift = strtoul(ArgV[1], NULL, 0);
                ++ArgV;
            }

            ++ArgV;

            if (log)
            {
                fprintf(log, "UT_BSP_Setup: iteration end ArgC=%lu ArgV=%p\n", (unsigned long)ArgC, (void *)ArgV);
            }
        }
    }

    if (log)
    {
        fprintf(log, "UT_BSP_Setup: before final UserShift=%u\n", (unsigned)UserShift);
    }

    if (UserShift == UTASSERT_CASETYPE_NONE || UserShift >= UTASSERT_CASETYPE_MAX)
    {
        UserShift = UTASSERT_CASETYPE_PASS;
    }

    BSP_UT_Global.CurrVerbosity = (2 << UserShift) - 1;

    if (log)
    {
        fprintf(log, "UT_BSP_Setup: final CurrVerbosity=%lu\n", (unsigned long)BSP_UT_Global.CurrVerbosity);
        fclose(log);
    }

    UT_BSP_DoText(UTASSERT_CASETYPE_BEGIN, "UNIT TEST");
}
